import java.io.*;
import java.util.ArrayList;
import java.util.List;

public class ProcessExecutor {
    public List<IterationResult> doFork(BinaryLinkServer reader, List<String> commandString, File stdOut, File stdErr, boolean printOut, boolean printErr) throws BenchmarkException {
        try {
            // Start the process
            ProcessBuilder processBuilder = new ProcessBuilder(commandString);
            processBuilder.redirectErrorStream(false);
            processBuilder.redirectOutput(ProcessBuilder.Redirect.appendTo(stdOut));
            processBuilder.redirectError(ProcessBuilder.Redirect.appendTo(stdErr));
            Process process = processBuilder.start();

            // Read output and error streams
            Thread outputThread = new Thread(() -> {
                try (BufferedReader readerOut = new BufferedReader(new InputStreamReader(process.getInputStream()))) {
                    String line;
                    while ((line = readerOut.readLine()) != null) {
                        if (printOut) {
                            System.out.println(line);
                        }
                        reader.write(line);
                    }
                } catch (IOException e) {
                    e.printStackTrace();
                }
            });

            Thread errorThread = new Thread(() -> {
                try (BufferedReader readerErr = new BufferedReader(new InputStreamReader(process.getErrorStream()))) {
                    String line;
                    while ((line = readerErr.readLine()) != null) {
                        if (printErr) {
                            System.err.println(line);
                        }
                        reader.write(line);
                    }
                } catch (IOException e) {
                    e.printStackTrace();
                }
            });

            outputThread.start();
            errorThread.start();

            // Wait for the process to complete
            int exitCode = process.waitFor();

            // Check exit code
            if (exitCode != 0) {
                throw new BenchmarkException("Process failed with exit code: " + exitCode);
            }

            // Read remaining lines from streams
            outputThread.join();
            errorThread.join();

            // Read results from BinaryLinkServer
            List<IterationResult> results = new ArrayList<>();
            while (true) {
                String result = reader.read();
                if (result == null) break;
                results.add(IterationResult.fromString(result));
            }

            return results;
        } catch (IOException | InterruptedException e) {
            throw new BenchmarkException("Error executing process: " + e.getMessage(), e);
        }
    }
}

