    private final OutputReceiver errReceiver;
    private final ErrorReporter errLog;
    private final long startTime;
    private final String[] execCommand;
    private final String execCommandStr;
    private final boolean logExecution;

    public ExtCmd(Map<ExtCmdCondition, String> conditionsWithDescriptions, Set<ExtCmdEndedListener> extCmdEndedListenerSet, OutputReceiver outReceiver, OutputReceiver errReceiver, ErrorReporter errLog, String[] execCommand, String execCommandStr, boolean logExecution) {
        this.conditionsWithDescriptions = conditionsWithDescriptions;
        this.extCmdEndedListenerSet = extCmdEndedListenerSet;
        this.outReceiver = outReceiver;
        this.errReceiver = errReceiver;
        this.errLog = errLog;
        this.startTime = System.currentTimeMillis();
        this.execCommand = execCommand;
        this.execCommandStr = execCommandStr;
        this.logExecution = logExecution;
    }

    public OutputStream exec(ProcessBuilder.Redirect redirect, File workingDir, String... args) throws IOException {
        ProcessBuilder pb = new ProcessBuilder(args);
        if (workingDir != null) {
            pb.directory(workingDir);
        }
        pb.redirectErrorStream(false);
        pb.redirectOutput(outReceiver);
        pb.redirectError(errReceiver);
        pb.redirectInput(redirect);
        Process process = pb.start();
        return process.getOutputStream();
    }

    public OutputData syncProcess() throws IOException, InterruptedException {
        Process process = new ProcessBuilder(execCommand).start();
        int exitCode = process.waitFor();
        OutputData outputData = new OutputData();
        outputData.setStdout(outReceiver.getOutput());
        outputData.setStderr(errReceiver.getOutput());
        outputData.setExitCode(exitCode);
        for (ExtCmdEndedListener listener : extCmdEndedListenerSet) {
            listener.onCommandEnded(outputData);
        }
        return outputData;
    }

    public void checkForConditions() {
        for (Map.Entry<ExtCmdCondition, String> entry : conditionsWithDescriptions.entrySet()) {
            if (!entry.getKey().checkCondition()) {
                errLog.log(entry.getValue());
            }
        }
    }

    public static class OutputData {
        private String stdout;
        private String stderr;
        private int exitCode;

        public String getStdout() {
            return stdout;
        }

        public void setStdout(String stdout) {
            this.stdout = stdout;
        }

        public String getStderr() {
            return stderr;
        }

        public void setStderr(String stderr) {
            this.stderr = stderr;
        }

        public int getExitCode() {
            return exitCode;
        }

        public void setExitCode(int exitCode) {
            this.exitCode = exitCode;
        }
    }

    public interface ExtCmdCondition {
        boolean checkCondition();
    }

    public interface ExtCmdEndedListener {
        void onCommandEnded(OutputData data);
    }
}

