import redis.clients.jedis.Jedis;
import redis.clients.jedis.JedisCluster;
import redis.clients.jedis.JedisClientConfig;
import redis.clients.jedis.HostAndPort;
import redis.clients.jedis.exceptions.JedisConnectionException;
import redis.clients.jedis.exceptions.JedisDataException;
import redis.clients.jedis.exceptions.JedisUnavailableException;

import java.util.*;
import java.util.concurrent.ConcurrentHashMap;
import java.util.concurrent.atomic.AtomicInteger;

public class ClusterConnectionProvider {
    private final JedisClusterInfoCache cache;
    private final Map<String, ConnectionPool> connectionMap = new ConcurrentHashMap<>();
    private final AtomicInteger slotCount = new AtomicInteger(0);
    private final JedisClientConfig clientConfig;

    public ClusterConnectionProvider(JedisClusterInfoCache cache, JedisClientConfig clientConfig) {
        this.cache = cache;
        this.clientConfig = clientConfig;
    }

    public void initializeSlotsCache(Set<HostAndPort> seedNodes, JedisClientConfig clientConfig) {
        try {
            JedisCluster cluster = new JedisCluster(seedNodes, clientConfig);
            cache.initialize(cluster);
        } catch (JedisConnectionException | JedisDataException | JedisUnavailableException e) {
            throw new RuntimeException("Failed to initialize cluster slots cache", e);
        }
    }

    public Connection getConnection(CommandArguments commandArguments) {
        int slot = hashSlot(commandArguments.getKey());
        return getConnectionFromSlot(slot);
    }

    public Connection getConnection() {
        if (connectionMap.isEmpty()) {
            throw new RuntimeException("No connections available in the cluster");
        }
        Map<String, ConnectionPool> map = new HashMap<>(connectionMap);
        String key = new Random().nextInt(map.size()) + "";
        return map.get(key).getConnection();
    }

    public Connection getConnectionFromSlot(int slot) {
        if (slot < 0 || slot >= slotCount.get()) {
            throw new IllegalArgumentException("Invalid slot number: " + slot);
        }
        String nodeKey = "node_" + slot;
        ConnectionPool pool = connectionMap.get(nodeKey);
        if (pool == null) {
            throw new RuntimeException("No connection pool found for slot: " + slot);
        }
        return pool.getConnection();
    }

    public void renewSlotCache() {
        try {
            JedisCluster cluster = new JedisCluster(cache.getSeedNodes(), clientConfig);
            cache.renew(cluster);
        } catch (JedisConnectionException | JedisDataException | JedisUnavailableException e) {
            throw new RuntimeException("Failed to renew cluster slots cache", e);
        }
    }

    public void close() {
        for (ConnectionPool pool : connectionMap.values()) {
            pool.close();
        }
    }

    public Map<String, ConnectionPool> getConnectionMap() {
        return Collections.unmodifiableMap(connectionMap);
    }

    private int hashSlot(String key) {
        return Math.abs(key.hashCode()) % slotCount.get();
    }
}

