public class RedisSSLConnection {
    private final RedisClient redisClient;
    private final StatefulRedisConnection<String, String> connection;

    public RedisSSLConnection(String redisUri, String hostName, String truststorePath, String truststorePassword) {
        try {
            // Load truststore
            KeyStore trustStore = KeyStore.getInstance(KeyStore.getDefaultType());
            try (InputStream is = getClass().getClassLoader().getResourceAsStream(truststorePath)) {
                trustStore.load(is, truststorePassword.toCharArray());
