        Path path = Paths.get(filePath);
        fileChannel = FileChannel.open(path, java.nio.file.StandardOpenOption.READ);
        MappedByteBuffer buffer = fileChannel.map(java.nio.channels.FileChannel.MapMode.READ_ONLY, 0, fileChannel.size());
        mappedAddress = System.identityHashCode(buffer);
        fileSize = fileChannel.size();
    }

    public long getMappedAddress() {
        return mappedAddress;
    }

    public long getFileSize() {
        return fileSize;
    }

    public void close() throws IOException {
        if (fileChannel != null) {
            fileChannel.close();
        }
    }
}
