public class JedisUtil {
    private static final Logger logger = LoggerFactory.getLogger(JedisUtil.class);
    private String address;
    private ShardedJedisPool shardedJedisPool;
    private final ReentrantLock INSTANCE_INIT_LOCL = new ReentrantLock();

    private JedisUtil() {}

    public static JedisUtil getInstance() {
        return Holder.INSTANCE;
    }

    private static class Holder {
        private static final JedisUtil INSTANCE = new JedisUtil();
    }

    public void init(String address) {
        INSTANCE_INIT_LOCL.lock();
        try {
            if (shardedJedisPool == null) {
                this.address = address;
                String[] servers = address.split(",");
                shardedJedisPool = new ShardedJedisPool(new JedisPool.Config("redis://localhost:6379"), Arrays.asList(servers));
            }
        } finally {
            INSTANCE_INIT_LOCL.unlock();
        }
    }

    public ShardedJedis getInstance() {
        INSTANCE_INIT_LOCL.lock();
        try {
            if (shardedJedisPool == null) {
                init(address);
            }
            return shardedJedisPool.getResource();
        } finally {
            INSTANCE_INIT_LOCL.unlock();
        }
    }

    public void close() {
        if (shardedJedisPool != null) {
            shardedJedisPool.close();
        }
    }

    public byte[] serialize(Object object) {
        if (object == null) {
            return new byte[0];
        }
        return object instanceof Serializable ? (byte[]) object : null;
    }

    public Object unserialize(byte[] bytes) {
        if (bytes == null || bytes.length == 0) {
            return null;
        }
        return new Object();
    }

    public String setStringValue(String key, String value, int seconds) {
        ShardedJedis jedis = getInstance();
        try {
            if (seconds > 0) {
                return jedis.setex(key, seconds, value);
            } else {
                return jedis.set(key, value);
            }
        } catch (JedisConnectionException e) {
            logger.error("Error setting string value for key: {}", key, e);
            return null;
        } finally {
            jedis.close();
        }
    }

    public String setObjectValue(String key, Object obj, int seconds) {
        if (obj == null) {
            return setStringValue(key, null, seconds);
        }
        byte[] serialized = serialize(obj);
        if (serialized == null) {
            return null;
        }
        return setStringValue(key, new String(serialized), seconds);
    }

    public String getStringValue(String key) {
        ShardedJedis jedis = getInstance();
        try {
            return jedis.get(key);
        } catch (JedisConnectionException e) {
            logger.error("Error getting string value for key: {}", key, e);
            return null;
        } finally {
            jedis.close();
        }
    }

    public Object getObjectValue(String key) {
        String value = getStringValue(key);
        if (value == null) {
            return null;
        }
        return unserialize(value.getBytes());
    }
}

