import java.io.IOException;
import java.io.RandomAccessFile;
import java.nio.MappedByteBuffer;
import java.nio.channels.FileChannel;
import java.nio.file.Path;
import java.nio.file.StandardOpenOption;

public class ArrayLongLongMapMmap {
    private final Path path;
    private final FileChannel writeChannel;
    private final MappedByteBuffer[] segmentsArray;
    private final FileChannel readChannel;

    public ArrayLongLongMapMmap(Path path, boolean madvise) throws IOException {
        this.path = path;
        this.writeChannel = new RandomAccessFile(path.toFile(), "rw").getChannel();
        int segmentSize = 1 << 27; // 134,217,728 bytes
        int numSegments = (int) (writeChannel.size() + segmentSize - 1) / segmentSize;
        segmentsArray = new MappedByteBuffer[numSegments];
        for (int i = 0; i < numSegments; i++) {
            long position = i * segmentSize;
            if (madvise) {
                segmentsArray[i] = writeChannel.map(FileChannel.MapMode.READ_WRITE, position, segmentSize).load();
            } else {
                segmentsArray[i] = writeChannel.map(FileChannel.MapMode.READ_WRITE, position, segmentSize);
            }
        }
    }

    public void init() throws IOException {
        writeChannel.close();
        readChannel = new RandomAccessFile(path.toFile(), "r").getChannel();
        int segmentSize = 1 << 27;
        int numSegments = (int) (readChannel.size() + segmentSize - 1) / segmentSize;
        segmentsArray = new MappedByteBuffer[numSegments];
        for (int i = 0; i < numSegments; i++) {
            long position = i * segmentSize;
            segmentsArray[i] = readChannel.map(FileChannel.MapMode.READ_ONLY, position, segmentSize);
        }
    }

    public long get(long key) {
        int segmentIndex = (int) (key >> 32) & ((segmentsArray.length - 1));
        int offset = (int) (key & 0xFFFFFFFFL);
        MappedByteBuffer segment = segmentsArray[segmentIndex];
        segment.position(offset * 8);
        return (segment.getLong() & 0xFFFFFFFFFFFFFFFFL);
    }

    public void close() throws IOException {
        if (readChannel != null) {
            readChannel.close();
        }
        for (MappedByteBuffer buffer : segmentsArray) {
            if (buffer != null) {
                buffer.force();
                buffer.clear();
            }
        }
        if (path != null) {
            path.toFile().delete();
        }
    }
}
