import java.io.File;
import java.io.IOException;
import java.nio.MappedByteBuffer;
import java.nio.channels.FileChannel;
import java.nio.file.Path;
import java.nio.file.StandardOpenOption;
import java.util.concurrent.atomic.AtomicBoolean;

public class MarkFile implements AutoCloseable {
    private final File markFile;
    private final MappedByteBuffer mappedBuffer;
    private final UnsafeBuffer buffer;
    private final AtomicBoolean isClosed = new AtomicBoolean(false);

    public MarkFile(File file, boolean create, int version, int timestamp, int heartbeat, long epoch, EpochClock clock, IntConsumer onVersionMismatch, Consumer<String> onErrorMessage) {
        this.markFile = file;
        this.buffer = new UnsafeBuffer(mapNewOrExistingMarkFile(file, create, version, timestamp, heartbeat, clock.currentTimeMillis(), clock, onVersionMismatch, onErrorMessage));
        this.mappedBuffer = buffer.getBuffer();
    }

    public void close() {
        if (!isClosed.getAndSet(true)) {
            try {
                FileChannel channel = null;
                try {
                    channel = FileChannel.open(markFile.toPath(), StandardOpenOption.WRITE);
                    channel.close();
                } finally {
                    if (channel != null) {
                        channel.close();
                    }
                }
            } catch (IOException e) {
                // Handle exception if needed
            }
        }
    }

    private static MappedByteBuffer mapNewOrExistingMarkFile(File file, boolean create, int version, int timestamp, int heartbeat, long epoch, EpochClock clock, IntConsumer onVersionMismatch, Consumer<String> onErrorMessage) {
        try {
            ensureMarkFileLink(file, file, "markfile");
            FileChannel channel = FileChannel.open(file.toPath(), StandardOpenOption.CREATE, StandardOpenOption.WRITE, StandardOpenOption.READ);
            MappedByteBuffer buffer = channel.map(FileChannel.MapMode.READ_WRITE, 0, 16); // Assuming 16 bytes for version, timestamp, heartbeat, epoch
            int currentVersion = buffer.getInt(0);
            long currentTimestamp = buffer.getLong(4);
            int currentHeartbeat = buffer.getInt(12);
            long currentEpoch = buffer.getLong(16);

            if (currentVersion != version) {
                onVersionMismatch.accept(currentVersion);
                return null;
            }

            if (currentTimestamp < clock.currentTimeMillis() - 1000) {
                onErrorMessage.accept("Timestamp is too old");
                return null;
            }

            if (currentHeartbeat != heartbeat) {
                onVersionMismatch.accept(currentHeartbeat);
                return null;
            }

            if (currentEpoch != epoch) {
                onVersionMismatch.accept(currentEpoch);
                return null;
            }

            return buffer;
        } catch (IOException e) {
            // Handle exception if needed
            return null;
        }
    }

    private static void ensureMarkFileLink(File file, File target, String linkName) {
        try {
            Path path = file.toPath();
            Path targetPath = target.toPath();
            if (!path.equals(targetPath)) {
                if (Files.exists(path)) {
                    Files.delete(path);
                }
                Files.createSymbolicLink(path, targetPath, LinkOption.NOFOLLOW_LINKS);
            }
        } catch (IOException e) {
            // Handle exception if needed
        }
    }
}
