import org.slf4j.Logger;
import org.slf4j.LoggerFactory;
import org.springframework.beans.factory.annotation.Value;
import org.springframework.stereotype.Component;
import org.springframework.util.StringUtils;

import javax.annotation.PostConstruct;
import java.util.concurrent.atomic.AtomicInteger;

@Component
public class PlanConsumer {

    @Value("${rocketmq.consumer.group}")
    private String consumerGroup;

    @Value("${rocketmq.namesrv.addr}")
    private String namesrvAddr;

    private final Logger logger = LoggerFactory.getLogger(PlanConsumer.class);

    @PostConstruct
    public void defaultMQPushConsumer() {
        DefaultMQPushConsumer consumer = new DefaultMQPushConsumer(consumerGroup);
        consumer.setNamesrvAddr(namesrvAddr);
        consumer.subscribe("YourTopic", "*");

        // Set consumption offset strategy to reset to the beginning
        consumer.setConsumeFromWhere(DefaultMQPushConsumer.CONSUME_FROM_LAST_OFFSET);

        // Register message listener with retry logic
        consumer.registerMessageListener((MessageListenerConcurrently) (messages, context) -> {
            AtomicInteger retryCount = new AtomicInteger(0);
            while (retryCount.get() < 3) {
                try {
                    for (MessageExt message : messages) {
                        // Process message
                        logger.info("Received message: {}", new String(message.getBody()));
                    }
                    return ConsumeConcurrentlyStatus.CONSUME_SUCCESS;
                } catch (Exception e) {
                    logger.error("Failed to process message, retrying... {}", e.getMessage(), e);
                    if (retryCount.incrementAndGet() < 3) {
                        // Wait before retrying
                        Thread.sleep(1000);
                    } else {
                        return ConsumeConcurrentlyStatus.RECONSUME_LATER;
                    }
                }
            }
            return ConsumeConcurrentlyStatus.RECONSUME_LATER;
        });

        // Add consumer listener for lifecycle events
        consumer.addConsumerListener(new ConsumerListener() {
            @Override
            public boolean consumeMessageFailed(List<MessageExt> messages, ConsumeConcurrentlyContext context) {
                logger.warn("Message consumption failed, will retry: {}", messages);
                return false;
            }

            @Override
            public boolean consumeMessageSuccess(List<MessageExt> messages, ConsumeConcurrentlyContext context) {
                logger.info("Message consumed successfully: {}", messages);
                return true;
            }
        });

        // Start the consumer
        consumer.start();
        logger.info("RocketMQ consumer started");
    }
}

