import org.apache.rocketmq.client.consumer.listener.ConsumeConcurrentlyContext;
import org.apache.rocketmq.client.consumer.listener.ConsumedMessageContext;
import org.apache.rocketmq.client.exception.MQClientException;
import org.springframework.context.annotation.Bean;
import org.springframework.context.annotation.Configuration;
import org.springframework.core.task.TaskExecutor;

import java.util.concurrent.atomic.AtomicBoolean;

@Configuration
public class AliyunMqConfiguration {

    private final PaascloudProperties paascloudProperties;
    private final OptPushMessageListener optPushConsumer;
    private final TaskExecutor taskExecutor;

    public AliyunMqConfiguration(PaascloudProperties paascloudProperties, OptPushMessageListener optPushConsumer, TaskExecutor taskExecutor) {
        this.paascloudProperties = paascloudProperties;
        this.optPushConsumer = optPushConsumer;
        this.taskExecutor = taskExecutor;
    }

    @Bean
    public DefaultMQPushConsumer defaultMQPushConsumer() throws MQClientException {
        DefaultMQPushConsumer consumer = new DefaultMQPushConsumer(paascloudProperties.getConsumerGroup());
        consumer.setNamesrvAddr(paascloudProperties.getNameServer());
        consumer.setMessageModel(DefaultMQPushConsumer.MessageModel.CLUSTERING);
        consumer.setConsumeThreadNums(10);

        String[] topics = paascloudProperties.getTopics().split(",");
        for (String topic : topics) {
            consumer.subscribe(topic, "*");
        }

        consumer.registerMessageListener((MessageListenerConcurrently) (message, consumeConcurrentlyContext) -> {
            try {
                optPushConsumer.onMessage(message);
                return ConsumeConcurrentlyStatus.CONSUME_SUCCESS;
            } catch (Exception e) {
                return ConsumeConcurrentlyStatus.RECONSUME_LATER;
            }
        });

        AtomicBoolean started = new AtomicBoolean(false);
