import java.io.IOException;
import java.nio.ByteBuffer;
import java.nio.channels.FileChannel;
import java.nio.file.Path;
import java.util.concurrent.ExecutorService;
import java.util.concurrent.Executors;

public class PathResource {
    private final Path file;
    private final PathResourceManager manager;

    public PathResource(Path file, PathResourceManager manager) {
        this.file = file;
        this.manager = manager;
    }

    public void serve(Sender sender, HttpServerExchange exchange, IoCallback callback) {
        serveImpl(sender, exchange, 0, Long.MAX_VALUE, callback, true);
    }

    public void serveRange(Sender sender, HttpServerExchange exchange, long start, long end, IoCallback callback) {
        serveImpl(sender, exchange, start, end, callback, false);
    }

    public Long getContentLength() {
        try {
            return file.toFile().length();
        } catch (IOException e) {
            return null;
        }
    }

    private void serveImpl(Sender sender, HttpServerExchange exchange, long start, long end, IoCallback callback, boolean fullRequest) {
        try (FileChannel channel = FileChannel.open(file, StandardOpenOption.READ)) {
            long fileSize = channel.size();
            long contentLength = fileSize;
            if (!fullRequest) {
                contentLength = end - start + 1;
            }
            exchange.setContentLength(contentLength);
            exchange.setChunkedTransfer(true);
            exchange.setContentType("application/octet-stream");

            if (manager.getTransferSizeThreshold() > 0 && contentLength > manager.getTransferSizeThreshold()) {
                new TransferTask(sender, exchange, channel, start, end, callback).execute();
            } else {
                new ServerTask(sender, exchange, channel, start, end, callback).execute();
            }
        } catch (IOException e) {
            callback.onException(e);
        }
    }

    private static class BaseFileTask implements Runnable {
        protected final Sender sender;
        protected final HttpServerExchange exchange;
        protected final FileChannel channel;
        protected final long start;
        protected final long end;
        protected final IoCallback callback;

        public BaseFileTask(Sender sender, HttpServerExchange exchange, FileChannel channel, long start, long end, IoCallback callback) {
            this.sender = sender;
            this.exchange = exchange;
            this.channel = channel;
            this.start = start;
            this.end = end;
            this.callback = callback;
        }

        protected void safeClose() {
            try {
                if (channel != null) {
                    channel.close();
                }
            } catch (IOException e) {
                callback.onException(e);
            }
        }

        @Override
        public void run() {
            try {
                callback.onSuccess();
            } catch (Exception e) {
                callback.onException(e);
            } finally {
                safeClose();
            }
        }
    }

    private static class ServerTask extends BaseFileTask {
        public ServerTask(Sender sender, HttpServerExchange exchange, FileChannel channel, long start, long end, IoCallback callback) {
            super(sender, exchange, channel, start, end, callback);
        }

        @Override
        public void run() {
            try {
                ByteBuffer buffer = PooledByteBuffer.allocate(8192);
                channel.position(start);
                while (channel.position() <= end) {
                    int bytesRead = channel.read(buffer);
                    if (bytesRead == -1) break;
                    buffer.flip();
                    sender.send(buffer, exchange);
                    buffer.clear();
                }
            } catch (IOException e) {
                callback.onException(e);
            } finally {
                super.run();
            }
        }
    }

    private static class TransferTask extends BaseFileTask {
        private final ExecutorService executor = Executors.newSingleThreadExecutor();

        public TransferTask(Sender sender, HttpServerExchange exchange, FileChannel channel, long start, long end, IoCallback callback) {
            super(sender, exchange, channel, start, end, callback);
        }

        @Override
        public void run() {
            try {
                channel.position(start);
                ByteBuffer buffer = PooledByteBuffer.allocate(8192);
                while (channel.position() <= end) {
                    int bytesRead = channel.read(buffer);
                    if (bytesRead == -1) break;
                    buffer.flip();
                    sender.send(buffer, exchange);
                    buffer.clear();
                }
            } catch (IOException e) {
                callback.onException(e);
            } finally {
                super.run();
            }
        }
    }
}

