            long currentTime = System.currentTimeMillis();
            long delay = currentTime - deliveryTime;
            System.out.println("Message delivered at: " + deliveryTime + ", current time: " + currentTime + ", delay: " + delay + " ms");
            return ConsumeConcurrentlyStatus.CONSUME_SUCCESS;
        });
        consumer.start();
    }

    public Options buildCommandlineOptions(Options options) {
        options.addOption("t", "topic", true, "RocketMQ topic to subscribe to");
        options.addOption("n", "namesrv", true, "NameServer address for RocketMQ");
        return options;
    }
}

