import org.apache.flink.api.common.serialization.KeyValueSerializationSchema;
import org.apache.flink.configuration.Configuration;
import org.apache.rocketmq.client.producer.DefaultMQProducer;
import org.apache.rocketmq.common.message.Message;
import java.util.List;
import java.util.Properties;

public class RocketMQSink<T> {
    private final Logger LOG;
    private final DefaultMQProducer producer;
    private boolean async;
    private Properties props;
    private TopicSelector<T> topicSelector;
    private KeyValueSerializationSchema<T> serializationSchema;
    private boolean batchFlushOnCheckpoint;
    private int batchSize;
    private List<Message> batchList;
    private int messageDeliveryDelayLevel;

    public RocketMQSink(KeyValueSerializationSchema<T> serializationSchema, TopicSelector<T> topicSelector, Properties props) {
        this.serializationSchema = serializationSchema;
        this.topicSelector = topicSelector;
        this.props = props;
        this.LOG = org.slf4j.LoggerFactory.getLogger(RocketMQSink.class);
        this.async = false;
        this.batchFlushOnCheckpoint = false;
        this.batchSize = 100;
        this.batchList = new java.util.ArrayList<>();
        this.messageDeliveryDelayLevel = 0;
    }

    public void open(Configuration parameters) throws Exception {
        producer = new DefaultMQProducer(props.getProperty("rocketmq.producer.group"));
        producer.setNamesrvAddr(props.getProperty("rocketmq.namesrv.addr"));
        producer.setVipChannelEnabled(false);
        producer.start();
    }

    public void invoke(T element) {
        if (batchFlushOnCheckpoint) {
            batchList.add(prepareMessage(element));
            if (batchList.size() >= batchSize) {
                flushSync();
            }
        } else {
            if (async) {
                producer.sendAsync(prepareMessage(element));
            } else {
                producer.send(prepareMessage(element));
            }
        }
    }

    private Message prepareMessage(T element) {
        String topic = topicSelector.selectTopic(element);
        String tag = topicSelector.selectTag(element);
        byte[] body = serializationSchema.serialize(element).getBytes();
        Message message = new Message(topic, tag, body);
        message.setDelayTimeLevel(messageDeliveryDelayLevel);
        return message;
    }

    public void close() throws Exception {
        if (producer != null) {
            producer.shutdown();
        }
    }

    public void flushSync() {
        if (batchFlushOnCheckpoint) {
            for (Message message : batchList) {
                producer.send(message);
            }
            batchList.clear();
        }
    }
}

