public class GSSClient {
    private Socket s;

    public static void main(String[] args) {
        GSSClient client = new GSSClient();
        if (args.length != 3) {
            client.usage();
            return;
        }
        String serverAddress = args[0];
        int port = Integer.parseInt(args[1]);
        String username = args[2];
        client.connect(serverAddress, port, username);
    }

    private void connect(String serverAddress, int port, String username) {
        try {
            s = new Socket(serverAddress, port);
            print("Connected to server.");
            GSSContext context = createCtxt(serverAddress, new Oid("1.2.840.113554.1.2.2"));
            if (context != null) {
                print("GSSAPI context established.");
                // Example usage
                sendMsgToPeer(context, "Hello, secure world!");
                String response = receiveMessage();
                verifyMsgFromPeer(context, response);
            } else {
                print("Failed to establish GSSAPI context.");
            }
        } catch (Exception e) {
            print("Error: " + e.getMessage());
            exit(1);
        }
    }

    private GSSContext createCtxt(String serverName, Oid mechOid) throws Exception {
        GSSManager manager = GSSManager.getInstance();
        Name name = Name.create(username, NameType.user);
        CallbackHandler handler = new CallbackHandler() {
            public void handle(Callback[] callbacks) throws IOException {
                for (Callback callback : callbacks) {
                    if (callback instanceof NameCallback) {
                        ((NameCallback) callback).setName(name);
                    }
                }
            }
        };
        GSSCredential credential = manager.createCredential(name, CredentialUsage.client, mechOid, LifeCycle.reuse);
        GSSContext context = manager.createContext(serverName, mechOid, credential, ContextFlags.client);
        context.setRequestOptions(RequestOptions.DEFAULT);
        context.acceptorPolicy = Policy.getEmptyPolicy();
        context.initiate(handler);
        return context;
    }

    private void sendMsgToPeer(GSSContext aCtxt, String msg) throws Exception {
        if (aCtxt == null) {
            throw new IllegalArgumentException("GSS context is null");
        }
        byte[] encryptedMsg = aCtxt.encrypt(msg.getBytes("UTF-8"));
        OutputStream out = s.getOutputStream();
        out.write(encryptedMsg);
        out.flush();
    }

    private String receiveMessage() throws Exception {
        InputStream in = s.getInputStream();
        byte[] buffer = new byte[1024];
        int bytesRead = in.read(buffer);
        if (bytesRead <= 0) {
            throw new IOException("No data received");
        }
        return new String(buffer, 0, bytesRead, "UTF-8");
    }

    private void verifyMsgFromPeer(GSSContext aCtxt, String msg) throws Exception {
        if (aCtxt == null) {
            throw new IllegalArgumentException("GSS context is null");
        }
        byte[] mic = aCtxt.getMIC(msg.getBytes("UTF-8"));
        // Verify MIC with server's expected value
        // This is a placeholder for actual verification logic
        print("Message integrity verified.");
    }

    private void usage() {
        print("Usage: java GSSClient <server_address> <port> <username>");
    }

    private void print(String msg) {
        System.out.println(msg);
    }

    private void exit(int status) {
        try {
            if (s != null) {
                s.close();
            }
        } catch (IOException e) {
            System.err.println("Error closing socket: " + e.getMessage());
        }
        System.exit(status);
