            // Create queue destination
            Destination queueDestination = session.createQueue("TEST.QUEUE");
            MessageProducer queueProducer = session.createProducer(queueDestination);

            // Create topic destination
            Destination topicDestination = session.createTopic("TEST.TOPIC");
            MessageProducer topicProducer = session.createProducer(topicDestination);

            sendMessage(session, queueProducer, "QUEUE");
            sendMessage(session, topicProducer, "TOPIC");

            session.commit();
        } catch (JMSException e) {
            e.printStackTrace();
        } finally {
            if (session != null) {
                try {
