import java.io.*;
import java.net.*;

public class TProfilerClient {
    private Socket socket;
    private PrintWriter writer;
    private BufferedReader reader;

    public void start(String server, int port) throws IOException {
        doSend("START", server, port);
    }

    public void stop(String server, int port) throws IOException {
        doSend("STOP", server, port);
    }

    public String status(String server, int port) throws IOException {
        return getStatus("STATUS", server, port);
    }

    public void flushMethod(String server, int port) throws IOException {
        doSend("FLUSHMETHOD", server, port);
    }

    private void doSend(String command, String server, int port) throws IOException {
        try {
            socket = new Socket(server, port);
            writer = new PrintWriter(socket.getOutputStream(), true);
            writer.println(command);
        } catch (IOException e) {
            throw new IOException("Failed to send command: " + command);
        }
    }

    private String getStatus(String command, String server, int port) throws IOException {
        try {
            socket = new Socket(server, port);
            writer = new PrintWriter(socket.getOutputStream(), true);
            writer.println(command);
            reader = new BufferedReader(new InputStreamReader(socket.getInputStream()));
            String response = read(reader);
            return response;
        } catch (IOException e) {
            throw new IOException("Failed to get status: " + command);
        } finally {
            if (socket != null) {
                socket.close();
            }
        }
    }

    private String read(BufferedReader in) throws IOException {
        StringBuilder response = new StringBuilder();
        String line;
        while ((line = in.readLine()) != null && !line.isEmpty()) {
            response.append(line).append("\n");
        }
        return response.toString();
    }
}

