import java.nio.channels.SocketChannel;
import java.util.Map;
import java.util.HashMap;
import java.util.concurrent.atomic.AtomicInteger;
import java.util.Date;
import java.util.logging.Logger;

public class NioTcpClient {
    private final Selector nioSelector;
    private final Map<String, SelectionKey> keyMap;
    private final Time time;
    private final SSLFactory sslFactory;
    private final NetworkConfig networkConfig;
    private static final Logger logger = Logger.getLogger(NioTcpClient.class.getName());
    private final AtomicInteger connectionIdCounter = new AtomicInteger(0);

    public NioTcpClient(Time time, SSLFactory sslFactory, NetworkConfig networkConfig) {
        this.time = time;
        this.sslFactory = sslFactory;
        this.networkConfig = networkConfig;
        this.nioSelector = Selector.open();
        this.keyMap = new HashMap<>();
    }

    public String connect(InetSocketAddress address, int bufferSize, int readTimeout, PortType portType) {
        String connectionId = generateConnectionId(null);
        SocketChannel socketChannel = SocketChannel.open(address);
        socketChannel.configureBlocking(false);
        socketChannel.setOption(ChannelOption.SO_RCVBUF, bufferSize);
        socketChannel.setOption(ChannelOption.SO_SNDBUF, bufferSize);
        socketChannel.setOption(ChannelOption.SO_TIMEOUT, readTimeout);

        SelectionKey selectionKey = socketChannel.register(nioSelector, SelectionKey.OP_CONNECT);
        keyMap.put(connectionId, selectionKey);

        if (portType == PortType.SSL) {
            try {
                sslFactory.setupSSL(socketChannel);
            } catch (Exception e) {
                logger.severe("Failed to setup SSL for connection: " + connectionId);
                disconnect(connectionId);
                return null;
            }
        }

        return connectionId;
    }

    private String generateConnectionId(SocketChannel socketChannel) {
        if (socketChannel != null) {
            InetSocketAddress local = (InetSocketAddress) socketChannel.getLocalAddress();
            InetSocketAddress remote = (InetSocketAddress) socketChannel.getRemoteAddress();
            return local.getHostString() + ":" + local.getPort() + "-" + remote.getHostString() + ":" + remote.getPort();
        } else {
            return "connection-" + connectionIdCounter.incrementAndGet();
        }
    }

    public Transmission createTransmission(String connectionId, String data, SelectionKey selectionKey, int bufferSize, PortType portType, SSLFactory.Mode sslMode) {
        SocketChannel socketChannel = (SocketChannel) selectionKey.channel();
        if (portType == PortType.SSL) {
            return sslFactory.createSSLTransmission(socketChannel, data, sslMode);
        } else {
            return new PlainTextTransmission(socketChannel, data, bufferSize);
        }
    }

    public void disconnect(String connectionId) {
        SelectionKey key = keyMap.remove(connectionId);
        if (key != null) {
            try {
                SocketChannel channel = (SocketChannel) key.channel();
                channel.close();
            } catch (IOException e) {
                logger.warning("Error closing connection: " + connectionId + " - " + e.getMessage());
            }
        }
    }

    public void close() {
        try {
            for (Map.Entry<String, SelectionKey> entry : keyMap.entrySet()) {
                disconnect(entry.getKey());
            }
            nioSelector.close();
        } catch (IOException e) {
            logger.severe("Error closing NIO selector: " + e.getMessage());
        }
    }
}

