import java.io.IOException;
import java.net.InetAddress;
import java.net.InetSocketAddress;
import java.nio.ByteBuffer;
import java.nio.channels.SelectionKey;
import java.nio.channels.SocketChannel;
import java.util.Map;
import java.util.Queue;
import java.util.concurrent.atomic.AtomicReference;
import java.util.concurrent.ConcurrentLinkedQueue;

public class PassiveRedisIndexer {
    private final Queue<ByteBuffer> writeQueue = new ConcurrentLinkedQueue<>();
    private final AtomicReference<SocketHandler> ref = new AtomicReference<>();
    private final NIOConnectionManager conMan;
    private final ConfigReader config;
    private final Map<String, String> namespaces;

    public PassiveRedisIndexer(NIOConnectionManager conMan, ConfigReader config, Map<String, String> namespaces) {
        this.conMan = conMan;
        this.config = config;
        this.namespaces = namespaces;
    }

    public InetAddress getAddress() {
        return config.getServerAddress();
    }

    private class SocketHandler {
        private final SocketChannel chan;
        private boolean awaitingWriteNotification;
        private ByteBuffer toWrite;
        private ByteBuffer oblivion = ByteBuffer.allocate(1024);

        public SocketHandler() throws IOException {
            this.chan = SocketChannel.open();
            this.chan.configureBlocking(false);
            this.awaitingWriteNotification = false;
            this.toWrite = ByteBuffer.allocate(1024);
        }

        public void open() throws IOException {
            InetSocketAddress address = new InetSocketAddress(getAddress(), config.getServerPort());
            this.chan.connect(address);
        }

        public void close() throws IOException {
            if (this.chan != null) {
                this.chan.close();
            }
        }

        public SocketChannel getChannel() {
            return this.chan;
        }

        public void registrationEvent(NIOConnectionManager manager, SelectionKey key) {
            if (key.isConnectable()) {
                if (this.chan.isConnected()) {
                    this.chan.finishConnect();
                    manager.register(this, key, SelectionKey.OP_READ);
                } else {
                    this.chan.connect(new InetSocketAddress(getAddress(), config.getServerPort()));
                }
            } else if (key.isWritable()) {
                manager.unregister(this, key);
                manager.register(this, key, SelectionKey.OP_READ);
            } else if (key.isReadable()) {
                manager.unregister(this, key);
                manager.register(this, key, SelectionKey.OP_WRITE);
            }
        }

        public void selectionEvent(SelectionKey key) {
            if (key.isConnectable()) {
                if (this.chan.isConnectionPending()) {
                    this.chan.finishConnect();
                    manager.register(this, key, SelectionKey.OP_READ);
                }
            } else if (key.isWritable()) {
                if (this.toWrite.hasRemaining()) {
                    int bytesWritten = this.chan.write(this.toWrite);
                    if (bytesWritten > 0) {
                        this.toWrite.position(0);
                        this.toWrite.limit(bytesWritten);
                    } else {
                        this.awaitingWriteNotification = true;
                        manager.register(this, key, SelectionKey.OP_WRITE);
                    }
                } else {
                    manager.unregister(this, key);
                    manager.register(this, key, SelectionKey.OP_READ);
                }
            } else if (key.isReadable()) {
                this.oblivion.clear();
                int bytesRead = this.chan.read(this.oblivion);
                if (bytesRead > 0) {
                    this.oblivion.flip();
                    manager.register(this, key, SelectionKey.OP_WRITE);
                } else {
                    manager.unregister(this, key);
                    manager.register(this, key, SelectionKey.OP_READ);
                }
            }
        }

        public void read() {
            this.oblivion.clear();
            try {
                int bytesRead = this.chan.read(this.oblivion);
                if (bytesRead > 0) {
                    this.oblivion.flip();
                }
            } catch (IOException e) {
                e.printStackTrace();
            }
        }

        public void doStateChecks(long now) {
            if (now - this.chan.validOps() > 1000) {
                try {
                    this.chan.close();
                } catch (IOException e) {
                    e.printStackTrace();
                }
            }
        }

        public int calcInterestOps() {
            int interestOps = 0;
            if (this.chan.isConnecting()) {
                interestOps |= SelectionKey.OP_CONNECT;
            } else if (this.chan.isOpen()) {
                interestOps |= SelectionKey.OP_READ;
                if (this.writeQueue.peek() != null) {
                    interestOps |= SelectionKey.OP_WRITE;
                }
            }
            return interestOps;
        }
    }
}

