import java.io.IOException;
import java.net.InetSocketAddress;
import java.net.SocketAddress;
import java.nio.ByteBuffer;
import java.nio.channels.AsynchronousChannelGroup;
import java.nio.channels.AsynchronousSocketChannel;
import java.nio.channels.CompletionHandler;
import java.util.Queue;
import java.util.concurrent.ExecutorService;
import java.util.concurrent.Executors;

public class AsynchronousSocketChannelStream {
    private final ServerAddress serverAddress;
    private final InetAddressResolver inetAddressResolver;
    private final SocketSettings settings;
    private final AsynchronousChannelGroup group;

    public AsynchronousSocketChannelStream(ServerAddress serverAddress, InetAddressResolver inetAddressResolver, SocketSettings settings, AsynchronousChannelGroup group) {
        this.serverAddress = serverAddress;
        this.inetAddressResolver = inetAddressResolver;
        this.settings = settings;
        this.group = group;
    }

    public void openAsync(OperationContext operationContext, AsyncCompletionHandler<Void> handler) {
        Queue<SocketAddress> socketAddressQueue = new java.util.LinkedList<>();
        socketAddressQueue.add(serverAddress.getAddress());
        initializeSocketChannel(handler, socketAddressQueue);
    }

    private void initializeSocketChannel(AsyncCompletionHandler<Void> handler, Queue<SocketAddress> socketAddressQueue) {
        AsynchronousSocketChannel attemptConnectionChannel = null;
        if (group != null) {
            attemptConnectionChannel = AsynchronousSocketChannel.open(group);
        } else {
            attemptConnectionChannel = AsynchronousSocketChannel.open();
        }

        attemptConnectionChannel.configureBlocking(false);
        attemptConnectionChannel.setOption(AsynchronousSocketChannel.SOO_REUSEADDR, true);
        attemptConnectionChannel.setOption(AsynchronousSocketChannel.SO_TIMEOUT, settings.getSoTimeout());

        try {
            attemptConnectionChannel.connect(socketAddressQueue.peek(), new OpenCompletionHandler(handler, socketAddressQueue, attemptConnectionChannel));
        } catch (IOException e) {
            handler.failed(e, attemptConnectionChannel);
        }
    }

    private static class OpenCompletionHandler implements CompletionHandler<Void, Object> {
        private final AsyncCompletionHandler<Void> handler;
        private final Queue<SocketAddress> socketAddressQueue;
        private final AsynchronousSocketChannel attemptConnectionChannel;

        public OpenCompletionHandler(AsyncCompletionHandler<Void> handler, Queue<SocketAddress> socketAddressQueue, AsynchronousSocketChannel attemptConnectionChannel) {
            this.handler = handler;
            this.socketAddressQueue = socketAddressQueue;
            this.attemptConnectionChannel = attemptConnectionChannel;
        }

        @Override
        public void completed(Void result, Object attachment) {
            try {
                if (attemptConnectionChannel.isOpen()) {
                    AsynchronousSocketChannelAdapter adapter = new AsynchronousSocketChannelAdapter(attemptConnectionChannel);
                    handler.completed(result, adapter);
                } else {
                    handler.failed(new IOException("Connection failed"), attachment);
                }
            } catch (Exception e) {
                handler.failed(e, attachment);
            }
        }

        @Override
        public void failed(Throwable exc, Object attachment) {
            if (!socketAddressQueue.isEmpty()) {
                SocketAddress nextAddress = socketAddressQueue.poll();
                if (nextAddress != null) {
                    try {
                        attemptConnectionChannel.connect(nextAddress, this);
                    } catch (IOException e) {
                        handler.failed(e, attachment);
                    }
                } else {
                    handler.failed(exc, attachment);
                }
            } else {
                handler.failed(exc, attachment);
            }
        }
    }

    private static class AsynchronousSocketChannelAdapter {
        private final AsynchronousSocketChannel channel;

        public AsynchronousSocketChannelAdapter(AsynchronousSocketChannel channel) {
            this.channel = channel;
        }

        public Future<Void> read(ByteBuffer buffer, AsyncCompletionHandler<Void> handler) {
            return channel.read(buffer, null, new AsyncCompletionHandler<Void>(handler, channel));
        }

        public Future<Void> write(ByteBuffer buffer, AsyncCompletionHandler<Void> handler) {
            return channel.write(buffer, null, new AsyncCompletionHandler<Void>(handler, channel));
        }

        public boolean isOpen() {
            return channel.isOpen();
        }

        public void close() {
            try {
                channel.close();
            } catch (IOException e) {
                e.printStackTrace();
            }
        }
    }
}

