import java.util.concurrent.locks.ReentrantLock;

public class ClientController {
    private final BlockingQueue<Packet> inner;
    private final Object lock;
    private final InetSocketAddress remote;
    private Thread thread;
    private SocketChannel channel;
    private final ReentrantLock lock = new ReentrantLock();

    public ClientController(String host, int port) {
        this.inner = new java.util.LinkedList<>();
        this.lock = new Object();
        this.remote = new InetSocketAddress(host, port);
        this.channel = SocketChannel.open();
    }

    public void start() {
        if (thread == null || thread.isAlive()) {
            thread = new Thread(this::run);
            thread.start();
        }
    }

    public boolean put(Packet packet) {
        lock.lock();
        try {
            inner.add(packet);
            return true;
        } finally {
            lock.unlock();
        }
    }

    public void clear() {
        lock.lock();
        try {
            inner.clear();
        } finally {
            lock.unlock();
        }
    }

    public void run() {
        try {
            channel.connect(remote);
            while (true) {
                write();
            }
        } catch (IOException e) {
            e.printStackTrace();
        } finally {
            try {
                if (channel != null) {
                    channel.close();
                }
            } catch (IOException e) {
                e.printStackTrace();
            }
        }
    }

    private void write() {
        lock.lock();
        try {
            if (!inner.isEmpty()) {
                Packet packet = inner.poll();
                if (packet != null) {
                    ByteBuffer buffer = ByteBuffer.allocate(packet.data.length + 4);
                    buffer.put(packet.data);
                    int checksum = calculateChecksum(packet.data);
                    buffer.putInt(checksum);
                    buffer.flip();
                    channel.write(buffer);
                }
            }
        } finally {
            lock.unlock();
        }
    }

    private int calculateChecksum(byte[] data) {
        int sum = 0;
        for (byte b : data) {
            sum += b & 0xFF;
        }
        return sum;
    }
}

