    private InetSocketAddress address;
    private DatagramChannel datagramChannel;
    private AtomicInteger failures;

    public GraphiteUDP(String hostname, int port) {
        this.hostname = hostname;
        this.port = port;
        this.failures = new AtomicInteger(0);
    }

    public void connect() throws IOException {
        datagramChannel = DatagramChannel.open();
        datagramChannel.configureBlocking(false);
        address = new InetSocketAddress(hostname, port);
        datagramChannel.socket().setOption(StandardSocketOptions.SO_REUSEADDR, true);
        datagramChannel.socket().setOption(StandardSocketOptions.SO_BROADCAST, true);
    }

    public boolean isConnected() {
        return datagramChannel != null && datagramChannel.isOpen();
    }

    public void send(String name, String value, long timestamp) throws IOException {
        if (!isConnected()) {
            throw new IOException("Not connected to Graphite server");
        }

        String metric = sanitize(name) + " " + sanitize(value) + " " + timestamp + "\n";
        ByteBuffer buffer = ByteBuffer.wrap(metric.getBytes());
        while (true) {
            try {
                datagramChannel.send(buffer, address);
                break;
            } catch (IOException e) {
                failures.incrementAndGet();
                if (failures.get() > 10) {
                    throw e;
                }
            }
        }
    }

    public int getFailures() {
        return failures.get();
    }

    public void flush() {
        // No action needed for UDP
    }

    public void close() throws IOException {
        if (datagramChannel != null && datagramChannel.isOpen()) {
            datagramChannel.close();
        }
    }

    public DatagramChannel getDatagramChannel() {
        return datagramChannel;
    }

    public void setDatagramChannel(DatagramChannel datagramChannel) {
        this.datagramChannel = datagramChannel;
    }

    public InetSocketAddress getAddress() {
        return address;
    }

    public void setAddress(InetSocketAddress address) {
        this.address = address;
    }

    private String sanitize(String s) {
        return GraphiteSanitize.sanitize(s);
    }
}

