    private static final int HEADER_LENGTH = 8;
    private static final int PEER_LENGTH = 28;
    private static final int PEERS_PER_PACKET = 50;

    public OpentrackerLiveSync() {
        id = new byte[4];
        // Generate a random 4-byte identifier
        new java.util.Random().nextBytes(id);
    }

    public void start() throws IOException {
        channel = DatagramChannel.open();
        channel.configureBlocking(false);
        channel.bind(new InetSocketAddress(0));
        channel.join(new InetSocketAddress("224.0.0.1", 5000), null);
        new Thread(this::send, "OpentrackerSender").start();
    }

    public void incomingPacket(MessageBase msg) {
