        while (true) {
            int bytesRead = channel.receive(buffer);
            if (bytesRead > 0) {
                buffer.flip();
                byte[] data = new byte[bytesRead];
                buffer.get(data);
                buffer.clear();
                return new String(data);
            }
        }
    }

