            DescribeConfigsResult result = _adminClient.describeConfigs(Collections.singletonList(new ConfigResource(ConfigResource.Type.TOPIC, topic)));
            result.all().get(DESCRIBE_CLUSTER_CONFIGS_TIMEOUT.toMillis(), TimeUnit.MILLISECONDS);
            Map<ConfigResource, Map<String, Config>> configs = result.all().get();
            if (configs.containsKey(new ConfigResource(ConfigResource.Type.TOPIC, topic))) {
                Map<String, Config> topicConfigMap = configs.get(new ConfigResource(ConfigResource.Type.TOPIC, topic));
                Properties properties = convertConfigToProperties(topicConfigMap);
                return properties;
            }
            return new Properties();
        } catch (Exception e) {
            LOG.warn("Failed to retrieve configurations for topic: {}", topic, e);
            return new Properties();
        }
    }

