        Map<String, Properties> result = new HashMap<>();
        for (String topic : topics) {
            Properties config = topicConfigs(topic);
            result.put(topic, config);
        }
        return result;
    }

    public Map<String, Properties> allTopicConfigs() {
        try {
            DescribeConfigsResult result = _adminClient.describeConfigs(_adminClient.listTopics().all().get().topicNames());
            result.all().get(DESCRIBE_CLUSTER_CONFIGS_TIMEOUT.toMillis(), TimeUnit.MILLISECONDS);
            Map<ConfigResource, Map<String, Config>> configs = result.all().get();
            for (Map.Entry<ConfigResource, Map<String, Config>> entry : configs.entrySet()) {
                ConfigResource resource = entry.getKey();
                if (resource.type() == ConfigResource.Type.TOPIC) {
                    Properties properties = convertConfigToProperties(entry.getValue());
                    _clusterConfigs.put(resource.name(), properties);
                }
            }
            return _clusterConfigs;
        } catch (Exception e) {
            LOG.warn("Failed to retrieve configurations for all topics in the cluster", e);
            return new HashMap<>();
        }
    }

    private Properties convertConfigToProperties(Map<String, Config> configMap) {
        Properties properties = new Properties();
        for (Map.Entry<String, Config> entry : configMap.entrySet()) {
            if (entry.getValue() != null) {
                properties.put(entry.getKey(), entry.getValue().value());
            }
        }
        return properties;
    }
}

