import org.java_websocket.client.WebSocketClient;
import org.java_websocket.handshake.ServerHandshake;
import javax.net.ssl.SSLContext;
import javax.net.ssl.SSLSocketFactory;
import javax.net.ssl.SSLParameters;
import javax.net.ssl.TrustManager;
import javax.net.ssl.X509TrustManager;
import java.net.URI;
import java.security.SecureRandom;
import java.security.KeyManagementException;
import java.security.NoSuchAlgorithmException;
import java.security.cert.X509Certificate;

public class SSLWebSocketClient extends WebSocketClient {

    private String endpointIdentificationAlgorithm;
    private boolean onSSLError = false;
    private boolean onOpen = false;

    public SSLWebSocketClient(String address, int port, String endpointIdentificationAlgorithm) {
        super(new URI("wss://" + address + ":" + port));
        this.endpointIdentificationAlgorithm = endpointIdentificationAlgorithm;
        configureSSL();
    }

    private void configureSSL() {
        try {
            SSLContext sslContext = SSLContext.getInstance("TLS");
            TrustManager[] trustAllCerts = new TrustManager[]{new X509TrustManager() {
                public void checkClientTrusted(X509Certificate[] chain, String authType) {}
                public void checkServerTrusted(X509Certificate[] chain, String authType) {}
                public X509Certificate[] getAcceptedIssuers() { return new X509Certificate[0]; }
            }};
            sslContext.init(null, trustAllCerts, new SecureRandom());
            SSLSocketFactory sslSocketFactory = sslContext.getSocketFactory();
            setSSLSocketFactory(sslSocketFactory);
            onSetSSLParameters(new SSLParameters());
        } catch (NoSuchAlgorithmException | KeyManagementException e) {
            e.printStackTrace();
        }
    }

    @Override
    public void onOpen(ServerHandshake handshakedata) {
        onOpen = true;
    }

    @Override
    public void onMessage(String message) {
        System.out.println("Received: " + message);
    }

    @Override
    public void onClose(int code, String reason, boolean remote) {
        System.out.println("Connection closed with code " + code + ", reason: " + reason);
    }

    @Override
    public void onError(Exception ex) {
        onSSLError = true;
        System.err.println("Error occurred: " + ex.getMessage());
    }

    @Override
    public void onSetSSLParameters(SSLParameters sslParameters) {
        sslParameters.setEndpointIdentificationAlgorithm(endpointIdentificationAlgorithm);
    }
}

