import javax.websocket.OnClose;
import javax.websocket.OnOpen;
import javax.websocket.Session;
import javax.websocket.Endpoint;
import javax.websocket.server.ServerEndpoint;
import java.util.concurrent.ConcurrentHashMap;
import java.util.logging.Logger;

@ServerEndpoint("/whiteboard")
public class Whiteboard extends Endpoint {
    private static final Logger LOGGER = Logger.getLogger(Whiteboard.class.getName());
    private static final Object PRESENT = new Object();
    private static final ConcurrentHashMap<Session, Object> peers = new ConcurrentHashMap<>();

    @OnOpen
    public void onOpen(Session peer) {
        peers.put(peer, PRESENT);
        LOGGER.info("New client connected: " + peer.getId());
    }

    @OnClose
    public void onClose(Session peer) {
        peers.remove(peer);
        LOGGER.info("Client disconnected: " + peer.getId());
    }

    public void broadcastFigure(Figure figure, Session session) {
        for (Session client : peers.keySet()) {
            if (client != session) {
                try {
                    client.getBasicRemote().sendText(figure.toString());
                } catch (Exception e) {
                    LOGGER.severe("Error broadcasting figure to client: " + e.getMessage());
                }
            }
        }
    }

    public void broadcastSnapshot(ByteBuffer data, Session session) {
        for (Session client : peers.keySet()) {
            if (client != session) {
                try {
                    client.getBasicRemote().sendBinary(data);
                } catch (Exception e) {
                    LOGGER.severe("Error broadcasting snapshot to client: " + e.getMessage());
                }
            }
        }
    }
}

