    private CountDownLatch waitForConnectionClosed;
    private CountDownLatch waitForAllExpectedCommands;
    private Thread mockServerThread;
    private String host;
    private int port;

    public MockImapServer() {
        this.interactions = new ArrayDeque<>();
        this.waitForConnectionClosed = new CountDownLatch(1);
        this.waitForAllExpectedCommands = new CountDownLatch(1);
        this.host = "localhost";
        this.port = 0; // Let system assign an available port
    }

    public void start() throws Exception {
        ServerSocket serverSocket = new ServerSocket(port, 50, InetAddress.getByName(host));
        mockServerThread = new Thread(new MockServerThread(serverSocket, interactions, waitForConnectionClosed, waitForAllExpectedCommands));
        mockServerThread.start();
    }

    public void shutdown() {
        shouldStop();
        try {
            mockServerThread.join(500);
        } catch (InterruptedException e) {
            Thread.currentThread().interrupt();
        }
    }

    private class MockServerThread implements Runnable {
        private ServerSocket serverSocket;
        private Deque<ImapInteraction> interactions;
        private CountDownLatch waitForConnectionClosed;
        private CountDownLatch waitForAllExpectedCommands;

        public MockServerThread(ServerSocket serverSocket, Deque<ImapInteraction> interactions, CountDownLatch waitForConnectionClosed, CountDownLatch waitForAllExpectedCommands) {
            this.serverSocket = serverSocket;
            this.interactions = interactions;
            this.waitForConnectionClosed = waitForConnectionClosed;
            this.waitForAllExpectedCommands = waitForAllExpectedCommands;
        }

        @Override
        public void run() {
            try {
                while (true) {
                    Socket clientSocket = serverSocket.accept();
                    new Thread(new ClientHandler(clientSocket)).start();
                }
            } catch (Exception e) {
                e.printStackTrace();
            } finally {
                try {
                    serverSocket.close();
                } catch (IOException e) {
                    e.printStackTrace();
                }
                waitForConnectionClosed.countDown();
            }
        }

        private class ClientHandler implements Runnable {
            private Socket clientSocket;

            public ClientHandler(Socket clientSocket) {
                this.clientSocket = clientSocket;
            }

            @Override
            public void run() {
                try (
                    InputStream input = clientSocket.getInputStream();
                    OutputStream output = clientSocket.getOutputStream();
                    BufferedReader reader = new BufferedReader(new InputStreamReader(input));
                    PrintWriter writer = new PrintWriter(output, true)
                ) {
                    String line;
                    while ((line = reader.readLine()) != null) {
                        if (line.trim().isEmpty()) continue;
                        if (line.startsWith("QUIT")) {
                            writer.println("OK");
                            break;
                        }
                        handleInteractions();
                        readAdditionalCommands();
                    }
                    writer.println("OK");
                    waitForAllExpectedCommands.countDown();
                } catch (Exception e) {
                    e.printStackTrace();
                } finally {
                    try {
                        clientSocket.close();
                    } catch (IOException e) {
                        e.printStackTrace();
                    }
                }
            }

            private void handleInteractions() {
                if (!interactions.isEmpty()) {
                    ImapInteraction interaction = interactions.pollFirst();
                    if (interaction != null) {
                        String expectedCommand = interaction.getExpectedCommand();
                        String response = interaction.getResponse();
                        if (expectedCommand.equals(reader.readLine())) {
                            writer.println(response);
                        } else {
                            writer.println("BAD");
                        }
                    }
                }
            }

            private void readAdditionalCommands() {
                try {
                    while (true) {
                        String line = reader.readLine();
                        if (line == null || line.trim().isEmpty()) break;
                        if (line.startsWith("QUIT")) break;
                    }
                } catch (Exception e) {
                    e.printStackTrace();
                }
            }
        }
    }

    private void shouldStop() {
        if (mockServerThread != null) {
            mockServerThread.interrupt();
        }
    }
}

