public class HttpMediaStreamer {
    private Uri uri;
    private ServerSocket serverSocket;
    private static final String TAG = "HttpMediaStreamer";

    public HttpMediaStreamer(String filename, String mimeType) {
        uri = new Uri("http", "localhost", 8080, "/stream/" + filename);
        try {
            serverSocket = new ServerSocket(8080);
            new Thread(this::startStreaming).start();
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    public Uri getUri() {
        return uri;
    }

    public void destroy() {
        try {
            if (serverSocket != null) {
                serverSocket.close();
            }
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    private void startStreaming() {
        while (true) {
            try {
                Socket clientSocket = serverSocket.accept();
                handleClient(clientSocket, filename, mimeType);
            } catch (IOException e) {
                e.printStackTrace();
                break;
            }
        }
    }

    private void handleClient(Socket clientSocket, String filename, String mimeType) throws IOException {
        PrintWriter out = new PrintWriter(clientSocket.getOutputStream(), true);
        BufferedReader in = new BufferedReader(new InputStreamReader(clientSocket.getInputStream()));
        String requestLine = in.readLine();
        if (requestLine == null || !requestLine.startsWith("GET /stream/")) {
            out.println("HTTP/1.1 400 Bad Request");
            out.println("Content-Type: text/plain");
            out.println("Connection: close");
            out.println();
            out.println("Invalid request");
            clientSocket.close();
            return;
        }

        String requestedFile = requestLine.substring("GET /stream/".length());
        if (!requestedFile.endsWith(".mp4") && !requestedFile.endsWith(".avi") && !requestedFile.endsWith(".mkv")) {
            out.println("HTTP/1.1 400 Bad Request");
            out.println("Content-Type: text/plain");
            out.println("Connection: close");
            out.println();
            out.println("Unsupported file type");
            clientSocket.close();
            return;
        }

        File file = new File(filename + requestedFile);
        if (!file.exists()) {
            out.println("HTTP/1.1 404 Not Found");
            out.println("Content-Type: text/plain");
            out.println("Connection: close");
            out.println();
            out.println("File not found");
            clientSocket.close();
            return;
        }

        out.println("HTTP/1.1 200 OK");
        out.println("Content-Type: " + mimeType);
        out.println("Content-Length: " + file.length());
        out.println("Connection: close");
        out.println();
        FileInputStream fis = new FileInputStream(file);
        byte[] buffer = new byte[4096];
        int bytesRead;
        while ((bytesRead = fis.read(buffer)) != -1) {
            out.write(buffer, 0, bytesRead);
            out.flush();
        }
        fis.close();
        clientSocket.close();
    }

    public Uri create(String filename, String mimeType) {
        uri = new Uri("http", "localhost", 8080, "/stream/" + filename);
        try {
            serverSocket = new ServerSocket(8080);
            new Thread(this::startStreaming).start();
        } catch (IOException e) {
            e.printStackTrace();
        }
        return uri;
    }
}

