        socket = new Socket(server, PORT);
        in = new BufferedInputStream(socket.getInputStream());
        out = socket.getOutputStream();
    }

    public void send(byte[] msgBytes) throws IOException {
        if (msgBytes.length > MAX_MSG_LEN) {
            throw new IllegalArgumentException("Message length exceeds maximum allowed: " + MAX_MSG_LEN);
        }
        // Write message length as 2 bytes
        lenBuf[0] = (byte) (msgBytes.length >> 8);
        lenBuf[1] = (byte) (msgBytes.length & 0xFF);
        out.write(lenBuf);
        out.write(msgBytes);
        out.flush();
    }

    public byte[] receive(boolean wait) throws IOException {
        msgBytes = new byte[MAX_MSG_LEN];
        msgLen = 0;
        while (msgLen < 2) {
            if (!wait) {
                return null;
