            int read = in.read(msgBytes, bytesRead, msgLength - bytesRead);
            if (read <= 0) {
                return null;
            }
            bytesRead += read;
        }
        return msgBytes;
    }

    public void close() throws IOException {
        if (socket != null) {
            socket.close();
        }
