                    e.printStackTrace();
                } finally {
                    try {
                        client.close();
                    } catch (IOException e) {
                        e.printStackTrace();
                    }
                }
            });
            return "Server started on port " + port;
        } catch (IOException e) {
            e.printStackTrace();
            return null;
        }
    }

    public void onClose() {
        try {
            if (server != null) server.close();
            if (client != null) client.close();
            if (pool != null) pool.shutdown();
