import java.io.*;
import java.net.*;
import java.nio.ByteBuffer;
import java.util.*;

public class WebSocketServer {
    private ServerSocket serverSocket;
    private int port;

    public WebSocketServer(int port) {
        this.port = port;
    }

    public void start() throws IOException {
        serverSocket = new ServerSocket(port);
        System.out.println("WebSocket server started on port " + port);

        while (true) {
            Socket socket = serverSocket.accept();
            new Thread(() -> handleConnection(socket)).start();
        }
    }

    public void stop() {
        if (serverSocket != null) {
            try {
                serverSocket.close();
            } catch (IOException e) {
                e.printStackTrace();
            }
        }
    }

    private void handleConnection(Socket socket) {
        try (
            InputStream inputStream = socket.getInputStream();
            OutputStream outputStream = socket.getOutputStream()
        ) {
            byte[] buffer = new byte[1024];
            int bytesRead;

            // Read the HTTP request
            StringBuilder request = new StringBuilder();
            while ((bytesRead = inputStream.read(buffer)) > 0) {
                request.append(new String(buffer, 0, bytesRead));
            }

            // Parse the request line
            String[] requestLines = request.toString().split("\r\n");
            String firstLine = requestLines[0];
            String[] firstLineParts = firstLine.split(" ");
            String method = firstLineParts[0];
            String path = firstLineParts[1];

            if (!method.equals("GET") || !path.contains("/ws")) {
                sendErrorResponse(socket, "HTTP/1.1", "400 Bad Request");
                return;
            }

            // Perform WebSocket handshake
            String response = "HTTP/1.1 101 Switching Protocols\r\n" +
                    "Upgrade: websocket\r\n" +
                    "Connection: upgrade\r\n" +
                    "Sec-WebSocket-Accept: " + getWebSocketAcceptKey(path) + "\r\n" +
                    "\r\n";
            outputStream.write(response.getBytes());
            outputStream.flush();

            // Send binary frames
            sendBinaryFrame(outputStream, true, 1024);
            sendBinaryFrame(outputStream, false, 1024);

        } catch (IOException e) {
            e.printStackTrace();
        } finally {
            if (socket != null) {
                try {
                    socket.close();
                } catch (IOException e) {
                    e.printStackTrace();
                }
            }
        }
    }

    private String getWebSocketAcceptKey(String path) {
        String key = path.substring(path.lastIndexOf("/") + 1);
        String hash = "258EAFA5-E914-47DA-95CA-C5AB0DC85B11";
        String combined = key + hash;
        byte[] digest = DigestUtils.sha1(combined.getBytes());
        StringBuilder hexString = new StringBuilder();
        for (byte b : digest) {
            String hex = Integer.toHexString(0xFF & b);
            if (hex.length() < 2) hex = "0" + hex;
            hexString.append(hex);
        }
        return hexString.toString();
    }

    private void sendBinaryFrame(OutputStream outputStream, boolean mask, int size) throws IOException {
        byte[] data = new byte[size];
        new Random().nextBytes(data);

        // Frame header
        byte frameHeader = 0x81; // FIN, Opcode 2 (binary)
        if (mask) {
            frameHeader |= 0x80; // Mask bit set
        }

        byte[] headerBytes = new byte[2];
        headerBytes[0] = (byte) (frameHeader >> 4);
        headerBytes[1] = (byte) (frameHeader & 0x0F);

        outputStream.write(headerBytes);
        outputStream.write(data);
        outputStream.flush();
    }
}

