    }

    public void start() {
        try {
            serverSocket = new ServerSocket(port);
            new Thread(this::run).start();
        } catch (IOException e) {
            System.err.println("Could not listen on port " + port);
            e.printStackTrace();
        }
    }

    public void stop() {
        try {
            if (serverSocket != null) {
                serverSocket.close();
            }
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    public void run() {
        while (true) {
            try {
                Socket clientSocket = serverSocket.accept();
                new Thread(() -> {
                    try {
                        BufferedReader in = new BufferedReader(new InputStreamReader(clientSocket.getInputStream()));
                        PrintWriter out = new PrintWriter(clientSocket.getOutputStream(), true);

                        String firstLine = in.readLine();
                        String[] parts = firstLine.split("\\s+");
                        String method = parts[0];
                        String path = parts[1];
                        HashMap<String, String> headers = new HashMap<>();
                        String line;

                        while (!(line = in.readLine()).isEmpty()) {
                            String[] headerParts = line.split(":\\s+");
                            if (headerParts.length == 2) {
                                headers.put(headerParts[0], headerParts[1]);
                            }
                        }

                        Map<String, String> queries = parsePath(path);
                        InputStream input = clientSocket.getInputStream();
                        OutputStream response = clientSocket.getOutputStream();

                        handle(method, path, headers, queries, input, response);

                        out.println("HTTP/1.1 200 OK");
                        out.println("Content-Type: text/plain");
                        out.println("Connection: close");
                        out.println();
                        out.println("Request processed.");
                    } catch (IOException e) {
                        e.printStackTrace();
                    } finally {
                        try {
                            clientSocket.close();
                        } catch (IOException e) {
                            e.printStackTrace();
                        }
                    }
                }).start();
            } catch (IOException e) {
                e.printStackTrace();
            }
        }
    }

    public abstract void handle(String method, String path, HashMap<String, String> headers, Map<String, String> queries, InputStream input, OutputStream response);

    public Map<String, String> parsePath(String path) {
        Map<String, String> params = new HashMap<>();
        int questionMarkIndex = path.indexOf('?');
        if (questionMarkIndex != -1) {
            String queryString = path.substring(questionMarkIndex + 1);
            String[] pairs = queryString.split("&");
            for (String pair : pairs) {
                String[] keyValue = pair.split("=");
                if (keyValue.length == 2) {
                    params.put(keyValue[0], keyValue[1]);
                }
            }
        }
        return params;
    }
}

