                            writeCannedResponse(new CannedResponse("250-" + host + " Hello there\n250-SIZE 10240000\n250-ENVELOPE\n250-8BITMIME\n250-DSN\n250-PIPELINING\n250-STARTTLS\n250-HELP\n"));
