        List<CobarNode> result = new ArrayList<>();
        byte[] buffer = new byte[1024];
        int bytesRead;
        while ((bytesRead = in.read(buffer)) != -1) {
            String line = new String(buffer, 0, bytesRead, "UTF-8");
            if (line.trim().isEmpty()) {
                continue;
            }
            result.add(new CobarNode(line));
        }
        return result;
    }

    public void close() {
        if (isClosed.get()) {
            return;
        }
        isClosed.set(true);
        try {
            if (out != null) {
                out.close();
            }
            if (in != null) {
                in.close();
            }
            if (socket != null) {
                socket.close();
            }
        } catch (IOException e) {
