        if (isClosed.get()) {
            throw new IllegalStateException("Connection is already closed.");
        }
        if (!isConnected()) {
            connect(10000);
        }
        byte[] commandBytes = command.getBytes("UTF-8");
        out.write(commandBytes);
        out.flush();
