    public Socket createSocket() throws IOException {
        if (isProxyNeeded()) {
            Socket socket = new Socket();
            connectToProxy(socket);
            return socket;
        } else {
            return super.createSocket();
        }
    }

    private boolean isProxyNeeded() {
        return proxyHost != null && proxyPort > 0 && proxyType != Proxy.Type.DIRECT;
    }

    private void connectToProxy(Socket socket) throws IOException {
        if (proxyType == Proxy.Type.HTTP) {
            socket.connect(new InetSocketAddress(proxyHost, proxyPort), 5000);
            socket.getOutputStream().write(("HTTP/1.1 " + "CONNECT " + proxyHost + ":" + proxyPort + " HTTP/1.1\r\n" +
                    "Host: " + proxyHost + ":" + proxyPort + "\r\n" +
                    "Connection: close\r\n\r\n").getBytes());
            InputStream in = socket.getInputStream();
            String response = readLine(in);
            if (!response.startsWith("HTTP/1.1 200")) {
                throw new IOException("Failed to connect to proxy: " + response);
            }
        }
    }

    private String getBasicAuthStr() {
        if (proxyUser == null || proxyPass == null) {
            return "";
        }
        return Base64.getEncoder().encodeToString((proxyUser + ":" + proxyPass).getBytes());
    }

    private String readLine(InputStream in) throws IOException {
        StringBuilder sb = new StringBuilder();
        int c;
        while ((c = in.read()) != -1) {
            if (c == '\r') {
                break;
            } else if (c == '\n') {
                break;
            } else {
                sb.append((char) c);
            }
        }
        return sb.toString();
    }

    private static int getResponseCode(String statusLine) {
        if (statusLine == null || statusLine.isEmpty()) {
            return -1;
        }
        String[] parts = statusLine.split("\\s+");
        if (parts.length < 2) {
            return -1;
        }
        try {
            return Integer.parseInt(parts[1]);
        } catch (NumberFormatException e) {
            return -1;
        }
    }
}

