import java.util.Arrays;

public class SntpImpl implements Sntp {
    private static final int NTP_PORT = 123;
    private static final int NTP_MODE = 3;
    private static final int NTP_VERSION = 3;
    private static final int NTP_PACKET_SIZE = 48;
    private static final long OFFSET_1900_TO_1970 = 2208988800L;

    public SntpResult requestTime(InetAddress server, float timeoutSeconds, float offsetThreshold, int packetRetries, int retryDelayMillis, SntpEventListener listener) {
        byte[] buffer = new byte[NTP_PACKET_SIZE];
        DatagramPacket packet = new DatagramPacket(buffer, buffer.length);
        int retries = 0;

        while (retries < packetRetries) {
            try {
                // Send NTP request
                packet.setAddress(server);
                packet.getPort(NTP_PORT);
                writeNtpVersion(buffer);
                writeTimeStamp(buffer, 0, System.currentTimeMillis() / 1000);
                DatagramSocket socket = new DatagramSocket();
                socket.send(packet);

                // Receive response
                packet = new DatagramPacket(buffer, buffer.length);
                socket.receive(packet);
                socket.close();

                // Validate response
                if (validateResponse(packet.getData(), packet.getLength(), offsetThreshold)) {
                    long timestamp = readTimeStamp(buffer, 40);
                    long ntpTime = timestamp - OFFSET_1900_TO_1970;
                    long currentTime = System.currentTimeMillis() / 1000;
                    long offset = currentTime - ntpTime;
                    double offsetMillis = doubleMillis(offset);
                    return new SntpResult(true, offsetMillis, ntpTime);
                } else {
                    listener.onInvalidResponse();
                }
            } catch (Exception e) {
                listener.onError(e.getMessage());
            }
            retries++;
            try {
                Thread.sleep(retryDelayMillis);
            } catch (InterruptedException ie) {
                Thread.currentThread().interrupt();
            }
        }
        return new SntpResult(false, 0, 0);
    }

    private void writeNtpVersion(byte[] buffer) {
        buffer[0] = (byte) NTP_VERSION;
        buffer[1] = (byte) NTP_MODE;
    }

    private void writeTimeStamp(byte[] buffer, int offset, long timestamp) {
        buffer[offset] = (byte) (timestamp >> 24);
        buffer[offset + 1] = (byte) (timestamp >> 16);
        buffer[offset + 2] = (byte) (timestamp >> 8);
        buffer[offset + 3] = (byte) timestamp;
    }

    private long readTimeStamp(byte[] buffer, int offset) {
        return ((buffer[offset] & 0xFF) << 24) |
               ((buffer[offset + 1] & 0xFF) << 16) |
               ((buffer[offset + 2] & 0xFF) << 8) |
               (buffer[offset + 3] & 0xFF);
    }

    private long read(byte[] buffer, int offset) {
        return ((buffer[offset] & 0xFF) << 24) |
               ((buffer[offset + 1] & 0xFF) << 16) |
               ((buffer[offset + 2] & 0xFF) << 8) |
               (buffer[offset + 3] & 0xFF);
    }

    private int ui(byte b) {
        return b >= 0 ? b : b + 256;
    }

    private double doubleMillis(long value) {
        return value / 1000.0;
    }

    private boolean validateResponse(byte[] data, int length, float offsetThreshold) {
        if (length < 48) return false;
        long transmitTimestamp = read(data, 40);
        long receiveTimestamp = read(data, 44);
        long offset = (transmitTimestamp - receiveTimestamp);
        return Math.abs(doubleMillis(offset)) <= offsetThreshold;
    }
}

