import java.net.DatagramPacket;
import java.net.DatagramSocket;
import java.net.InetAddress;
import java.net.SocketTimeoutException;
import java.net.PortUnreachableException;

public class UdpPortProber {
    public boolean probe(String host, PortMapping portMapping) {
        try {
            InetAddress inetAddress = InetAddress.getByName(host);
            int port = portMapping.getExternalPort();
            DatagramSocket socket = new DatagramSocket();
            socket.setSoTimeout(1000); // 1 second timeout

            byte[] sendBuffer = "PING".getBytes();
            DatagramPacket sendPacket = new DatagramPacket(sendBuffer, sendBuffer.length, inetAddress, port);
            socket.send(sendPacket);

            // Wait for response
            DatagramPacket receivePacket = new DatagramPacket(new byte[1024], 1024);
            socket.receive(receivePacket);

            // If a response is received, consider the port as unreachable (as per task description)
            return false;
        } catch (SocketTimeoutException e) {
            // Timeout occurred, consider the port as reachable
            return true;
        } catch (PortUnreachableException e) {
            // Port is definitely unreachable
            return false;
        } catch (Exception e) {
            // Other exceptions, consider the port as unreachable
            e.printStackTrace();
            return false;
        }
    }
}

