import java.io.OutputStream;
import java.net.DatagramPacket;
import java.net.DatagramSocket;
import java.net.InetAddress;

public class DropwizardUdpSocketAppender extends OutputStreamAppender {
    private String host;
    private int port;
    private DatagramSocket socket;

    public DropwizardUdpSocketAppender(String host, int port) {
        this.host = host;
        this.port = port;
    }

    @Override
    public void start() {
        try {
            socket = new DatagramSocket();
            socket.setSoTimeout(1000);
        } catch (Exception e) {
            e.printStackTrace();
        }
        super.start();
    }

    @Override
    public OutputStream datagramSocketOutputStream(String host, int port) {
        return new UdpOutputStream(socket, host, port);
    }

    private static class UdpOutputStream extends OutputStream {
        private final DatagramSocket socket;
        private final String host;
        private final int port;

        public UdpOutputStream(DatagramSocket socket, String host, int port) {
            this.socket = socket;
            this.host = host;
            this.port = port;
        }

        @Override
        public void write(int b) throws UnsupportedOperationException {
            throw new UnsupportedOperationException("UDP does not support byte-level writes");
        }

        @Override
        public void write(byte[] b, int off, int len) throws Exception {
            DatagramPacket packet = new DatagramPacket(b, off, off + len);
            packet.getAddress().setHost(InetAddress.getByName(host));
            packet.getPort() = port;
            socket.send(packet);
        }

        @Override
        public void close() throws Exception {
            socket.close();
        }
    }
}

