public class UDPServer {
    private DatagramSocket socket;
    private int port;

    public UDPServer(int port) throws IOException {
        this.port = port;
        this.socket = new DatagramSocket(port);
    }

    public void send(String message, String targetHost, int targetPort) throws IOException {
        byte[] data = message.getBytes();
        InetAddress address = InetAddress.getByName(targetHost);
        DatagramPacket packet = new DatagramPacket(data, data.length, address, targetPort);
        socket.send(packet);
    }

    public void close() {
        if (socket != null) {
            socket.close();
        }
    }
