import java.io.IOException;
import java.net.DatagramPacket;
import java.net.DatagramSocket;
import java.net.InetAddress;
import java.net.InetSocketAddress;
import java.net.MulticastSocket;
import java.util.ArrayList;
import java.util.List;

public class DnsUdpClient {
    private InetSocketAddress[] DNS_SERVERS;

    public DnsUdpClient(InetSocketAddress[] dnsServers) {
        this.DNS_SERVERS = dnsServers;
    }

    public List<Inet4Address> resolveAOverUdp(InetSocketAddress dnsServer, byte[] query) throws Exception {
        DatagramSocket socket = new DatagramSocket();
        DatagramPacket packet = new DatagramPacket(query, query.length, dnsServer);
        socket.send(packet);

        byte[] responseBuffer = new byte[1024];
        DatagramPacket responsePacket = new DatagramPacket(responseBuffer, responseBuffer.length);
        socket.receive(responsePacket);

        return toIps(responsePacket.getData());
    }

    private List<Inet4Address> toIps(byte[] buffer) {
        List<Inet4Address> ips = new ArrayList<>();
        int offset = 0;

        while (offset < buffer.length) {
            if (buffer[offset] == 0x00 && buffer[offset + 1] == 0x01) {
                int addressLength = buffer[offset + 2];
                for (int i = 0; i < addressLength; i++) {
                    byte b = buffer[offset + 3 + i];
                    ips.add((Inet4Address) InetAddress.getByAddress(new byte[]{b, buffer[offset + 3 + i + 1], buffer[offset + 3 + i + 2], buffer[offset + 3 + i + 3]}));
                }
                offset += 4 + addressLength;
            } else {
                offset += 1;
            }
        }

        return ips;
    }

    public String resolveTXTOverUdp(InetSocketAddress dnsServer, byte[] query) throws Exception {
        DatagramSocket socket = new DatagramSocket();
        DatagramPacket packet = new DatagramPacket(query, query.length, dnsServer);
        socket.send(packet);

        byte[] responseBuffer = new byte[1024];
        DatagramPacket responsePacket = new DatagramPacket(responseBuffer, responseBuffer.length);
        socket.receive(responsePacket);

        return toTXT(responsePacket.getData());
    }

    private String toTXT(byte[] buffer) throws RuntimeException {
        int offset = 0;
        while (offset < buffer.length) {
            if (buffer[offset] == 0x00 && buffer[offset + 1] == 0x02) {
                int length = buffer[offset + 2];
                int start = offset + 3;
                int end = start + length;
                String txtRecord = new String(buffer, start, length);
                return txtRecord;
            } else {
                offset += 1;
            }
        }
        throw new RuntimeException("No TXT record found in DNS response");
    }
}

