import java.io.BufferedReader;
import java.io.InputStreamReader;
import java.io.OutputStream;
import java.net.HttpURLConnection;
import java.net.URL;
import java.util.Map;
import org.apache.logging.log4j.Logger;

public class TinyIdHttpUtils {
    private static final Logger logger = org.apache.logging.log4j.LogManager.getLogger(TinyIdHttpUtils.class);

    public static String post(String url, Integer readTimeout, Integer connectTimeout) throws Exception {
        return post(url, null, readTimeout, connectTimeout);
    }

    public static String post(String url, Map<String, String> form, Integer readTimeout, Integer connectTimeout) throws Exception {
        if (url == null || url.isEmpty()) {
            throw new IllegalArgumentException("URL cannot be null or empty");
        }

        HttpURLConnection connection = null;
        try {
            URL obj = new URL(url);
            connection = (HttpURLConnection) obj.openConnection();
            connection.setRequestMethod("POST");

            if (readTimeout != null) {
                connection.setReadTimeout(readTimeout);
            }
            if (connectTimeout != null) {
                connection.setConnectTimeout(connectTimeout);
            }

            if (form != null && !form.isEmpty()) {
                StringBuilder formData = new StringBuilder();
                for (Map.Entry<String, String> entry : form.entrySet()) {
                    if (formData.length() > 0) {
                        formData.append("&");
                    }
                    formData.append(encode(entry.getKey())).append("=").append(encode(entry.getValue()));
                }
                connection.setDoOutput(true);
                try (OutputStream os = connection.getOutputStream()) {
                    byte[] input = formData.toString().getBytes("utf-8");
                    os.write(input);
                }
            }

            int responseCode = connection.getResponseCode();
            logger.info("POST Response Code : " + responseCode);

            BufferedReader in = new BufferedReader(new InputStreamReader(connection.getInputStream()));
            String inputLine;
            StringBuilder response = new StringBuilder();

            while ((inputLine = in.readLine()) != null) {
                response.append(inputLine);
            }
            in.close();
            return response.toString();
        } catch (Exception e) {
            logger.error("Error during POST request to " + url, e);
            throw e;
        } finally {
            if (connection != null) {
                connection.disconnect();
            }
        }
    }

    private static String encode(String value) {
        try {
            return java.net.URLEncoder.encode(value, "UTF-8");
        } catch (Exception e) {
            logger.warn("Error encoding parameter: " + value, e);
            return value;
        }
    }
}

