            } else {
                response = new OkHttpClient().newCall(new Request.Builder().url(url.toString()).build()).execute();
            }
            return response.body().bytes();
        } catch (IOException e) {
            throw e;
        } finally {
            connection.disconnect();
        }
    }

    private HttpURLConnection openConnection(URL url, Proxy proxy) throws MalformedURLException {
        if (!url.getProtocol().equals("http") && !url.getProtocol().equals("https")) {
            throw new MalformedURLException("Unsupported protocol: " + url.getProtocol());
        }
        HttpURLConnection connection = (HttpURLConnection) url.openConnection(proxy);
        connection.setInstanceFollowRedirects(false);
        connection.setConnectTimeout(10000);
        connection.setReadTimeout(10000);
        connection.setRequestProperty("User-Agent", "MMS/1.0");
        connection.setRequestProperty("Accept-Language", getCurrentAcceptLanguage(Locale.getDefault()));
        addExtraHeaders(connection, mmsConfig);
        return connection;
    }

    private void checkMethod(String method) {
        if (!method.equals("GET") && !method.equals("POST")) {
            throw new IllegalArgumentException("Invalid HTTP method: " + method);
        }
    }

    private String getCurrentAcceptLanguage(Locale locale) {
        if (locale == null) {
            return "en-US";
        }
        return locale.getLanguage() + "-" + locale.getCountry();
    }

    private void addExtraHeaders(HttpURLConnection connection, MmsConfig.Overridden mmsConfig) {
        if (mmsConfig != null) {
            connection.setRequestProperty("X-MMS-Config", mmsConfig.toString());
        }
    }

    private void logHttpHeaders(Map<String, List<String>> headers) {
        for (Map.Entry<String, List<String>> entry : headers.entrySet()) {
            System.out.println(entry.getKey() + ": " + String.join(", ", entry.getValue()));
        }
    }
}

