import java.io.*;
import java.net.*;
import java.security.cert.X509Certificate;
import javax.net.ssl.*;

public class HttpClient {
    private static final Config config = new Config();

    public String sendRequest(HttpRequestInfo httpRequestInfo, String cookieValue) {
        HttpURLConnection connection = createConnection(httpRequestInfo);
        configureRequest(connection, httpRequestInfo, cookieValue);
        sendRequestBody(connection, httpRequestInfo);
        try {
            int responseCode = connection.getResponseCode();
            if (responseCode == 200) {
                InputStream inputStream = connection.getInputStream();
                ByteArrayOutputStream baos = new ByteArrayOutputStream();
                byte[] buffer = new byte[1024];
                int bytesRead;
                while ((bytesRead = inputStream.read(buffer)) != -1) {
                    baos.write(buffer, 0, bytesRead);
                }
                return baos.toString();
            } else {
                return null;
            }
        } catch (IOException e) {
            e.printStackTrace();
            return null;
        } finally {
            closeResources(connection, null, null);
        }
    }

    private HttpURLConnection createConnection(HttpRequestInfo httpRequestInfo) throws IOException {
        Proxy proxy = config.getProxy();
        URL url = new URL(httpRequestInfo.getUrl());
        HttpURLConnection connection = (HttpURLConnection) url.openConnection(proxy);
        connection.setRequestMethod(httpRequestInfo.getMethod());
        connection.setConnectTimeout(config.getConnectionTimeout());
        connection.setReadTimeout(config.getReadTimeout());
        if (config.isSslVerificationBypass()) {
            SSLContext sslContext = SSLContext.getInstance("TLS");
            sslContext.init(null, new TrustManager[]{new X509TrustManager() {
                public void checkClientTrusted(X509Certificate[] chain, String authType) {}
                public void checkServerTrusted(X509Certificate[] chain, String authType) {}
                public X509Certificate[] getAcceptedIssuers() { return new X509Certificate[0]; }
            }, null);
            SSLSocketFactory sslSocketFactory = sslContext.getSocketFactory();
            connection.setSSLSocketFactory(sslSocketFactory);
        }
        return connection;
    }

    private void configureRequest(HttpURLConnection connection, HttpRequestInfo httpRequestInfo, String cookieValue) {
        Map<String, String> headers = httpRequestInfo.getHeaders();
        for (Map.Entry<String, String> entry : headers.entrySet()) {
            connection.setRequestProperty(entry.getKey(), entry.getValue());
        }
        configureCookie(headers, cookieValue);
    }

    private void configureCookie(Map<String, String> headers, String cookieValue) {
        if (cookieValue != null && !cookieValue.isEmpty()) {
            headers.put("Cookie", cookieValue);
        }
    }

    private void sendRequestBody(HttpURLConnection connection, HttpRequestInfo httpRequestInfo) throws IOException {
        if (httpRequestInfo.getBody() != null && httpRequestInfo.getMethod().equals("POST")) {
            connection.setDoOutput(true);
            OutputStream os = connection.getOutputStream();
            os.write(httpRequestInfo.getBody().getBytes());
            os.flush();
            os.close();
        }
    }

    private void closeResources(HttpURLConnection connection, InputStream inputStream, ByteArrayOutputStream baos) {
        if (connection != null) {
            connection.disconnect();
        }
        if (inputStream != null) {
            try {
                inputStream.close();
            } catch (IOException e) {
                e.printStackTrace();
            }
        }
        if (baos != null) {
            try {
                baos.close();
            } catch (IOException e) {
                e.printStackTrace();
            }
        }
    }
}
