import javax.jms.BytesMessage;
import javax.jms.Connection;
import javax.jms.Destination;
import javax.jms.ExceptionListener;
import javax.jms.JMSException;
import javax.jms.Message;
import javax.jms.MessageConsumer;
import javax.jms.Session;
import javax.jms.TextMessage;
import org.apache.activemq.ActiveMQConnectionFactory;
import com.sitewhere.spi.SiteWhereException;

public class ActiveMqConsumer implements Runnable, ExceptionListener {

    private Connection connection;
    private Session session;
    private MessageConsumer consumer;
    private String remoteUri;
    private String queueName;

    public ActiveMqConsumer(String remoteUri, String queueName) {
        this.remoteUri = remoteUri;
        this.queueName = queueName;
    }

    public void start() throws SiteWhereException {
        try {
            ActiveMQConnectionFactory connectionFactory = new ActiveMQConnectionFactory(remoteUri);
            this.connection = connectionFactory.createConnection();
            this.connection.setExceptionListener(this);
            this.connection.start();
            this.session = this.connection.createSession(false, Session.AUTO_ACKNOWLEDGE);
            Destination destination = this.session.createQueue(queueName);
            this.consumer = this.session.createConsumer(destination);
        } catch (Exception e) {
            throw new SiteWhereException("Error starting ActiveMQ consumer.", e);
        }
    }

    public void stop() throws SiteWhereException {
        try {
            this.consumer.close();
            this.session.close();
            this.connection.close();
        } catch (Exception e) {
            throw new SiteWhereException("Error shutting down ActiveMQ consumer.", e);
        }
    }

    @Override
    public void run() {
        while (true) {
            try {
                Message message = consumer.receive();
                if (message == null) {
                    break;
                }
                if (message instanceof TextMessage) {
                    TextMessage textMessage = (TextMessage) message;
                    byte[] payload = textMessage.getText().getBytes();
                    // Process payload here
                } else if (message instanceof BytesMessage) {
                    BytesMessage bytesMessage = (BytesMessage) message;
                    byte[] buffer = new byte[(int) bytesMessage.getBodyLength()];
                    // Process buffer here
                }
            } catch (Throwable e) {
                return;
            }
        }
    }

    @Override
    public void onException(JMSException e) {
        try {
            stop();
        } catch (SiteWhereException e1) {
        }
    }

    protected Connection getConnection() {
        return connection;
    }

    protected Session getSession() {
        return session;
    }

    protected MessageConsumer getConsumer() {
        return consumer;
    }
}

