class HttpClientException extends Exception {
    public HttpClientException(String message, Throwable cause) {
        super(message, cause);
    }
}

class HttpConnectionRetryHandler {
    public void handleRetry(HttpURLConnection connection) throws IOException {
        int retryCount = 0;
        int maxRetries = 3;
        IOException lastException = null;
        while (retryCount < maxRetries) {
            try {
                connection.connect();
                break;
            } catch (IOException e) {
                lastException = e;
                retryCount++;
                try {
                    Thread.sleep(1000);
                } catch (InterruptedException ie) {
                    Thread.currentThread().interrupt();
                }
            }
        }
        if (lastException != null) {
            throw lastException;
        }
    }
}

