import io.netty.channel.socket.nio.NioSocketChannel;
import io.netty.handler.codec.http.DefaultHttpHeaders;
import io.netty.handler.codec.http.HttpClientCodec;
import io.netty.handler.codec.http.HttpObjectAggregator;
import io.netty.handler.codec.http.websocketx.WebSocketClientHandshaker;
import io.netty.handler.codec.http.websocketx.WebSocketClientHandshakerFactory;
import io.netty.handler.codec.http.websocketx.WebSocketVersion;
import io.netty.handler.codec.http.websocketx.extensions.WebSocketClientExtensionHandler;
import io.netty.handler.codec.http.websocketx.extensions.compression.WebSocketClientCompressionHandler;
import io.netty.handler.logging.LogLevel;
import io.netty.handler.logging.LoggingHandler;
import io.netty.handler.proxy.Socks5ProxyHandler;
import io.netty.handler.ssl.SslContext;
import io.netty.handler.ssl.SslContextBuilder;
import io.netty.handler.ssl.util.InsecureTrustManagerFactory;
import io.netty.handler.timeout.IdleState;
import io.netty.handler.timeout.IdleStateEvent;
import io.netty.handler.timeout.IdleStateHandler;
import io.netty.util.internal.SocketUtils;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;

import java.net.URI;
import java.time.Duration;

public class NettyTcpClient {

    private static final Logger LOG = LoggerFactory.getLogger(NettyTcpClient.class);
    private static final Duration DEFAULT_CONNECTION_TIMEOUT = Duration.ofSeconds(10);
    private static final int DEFAULT_IDLE_TIMEOUT = 15;

    private URI uri;
    private Channel webSocketChannel;
    private NioEventLoopGroup eventLoopGroup;
    private boolean compressedMessages = false;
    private boolean acceptAllCertificates = false;
    private boolean enableLoggingHandler = false;
    private LogLevel loggingHandlerLevel = LogLevel.DEBUG;
    private String socksProxyHost;
    private Integer socksProxyPort;

    public void connect() {
        try {
            String scheme = uri.getScheme() == null ? "ws" : uri.getScheme();
            String host = uri.getHost();
            if (host == null) {
                throw new IllegalArgumentException("Host cannot be null.");
            }

            final int port = uri.getPort() == -1 ? 
                ("ws".equalsIgnoreCase(scheme) ? 80 : 443) : uri.getPort();

            final boolean ssl = "wss".equalsIgnoreCase(scheme);
            final SslContext sslCtx = ssl ? 
                SslContextBuilder.forClient()
                    .trustManager(acceptAllCertificates ? InsecureTrustManagerFactory.INSTANCE : null)
                    .build() : null;

            WebSocketClientHandler handler = getWebSocketClientHandler(
                WebSocketClientHandshakerFactory.newHandshaker(
                    uri, WebSocketVersion.V13, null, true, new DefaultHttpHeaders(), 65536),
                message -> {});

            if (eventLoopGroup == null || eventLoopGroup.isShutdown()) {
                eventLoopGroup = new NioEventLoopGroup(2);
            }

            new Bootstrap()
                .group(eventLoopGroup)
                .option(ChannelOption.CONNECT_TIMEOUT_MILLIS, 
                    (int) DEFAULT_CONNECTION_TIMEOUT.toMillis())
                .option(ChannelOption.SO_KEEPALIVE, true)
                .channel(NioSocketChannel.class)
                .handler(new ChannelInitializer<SocketChannel>() {
                    @Override
                    protected void initChannel(SocketChannel ch) {
                        ChannelPipeline p = ch.pipeline();
                        if (socksProxyHost != null) {
                            p.addLast(new Socks5ProxyHandler(
                                SocketUtils.socketAddress(socksProxyHost, socksProxyPort)));
                        }
                        if (sslCtx != null) {
                            p.addLast(sslCtx.newHandler(ch.alloc(), host, port));
                        }
                        p.addLast(new HttpClientCodec());
                        if (enableLoggingHandler) {
                            p.addLast(new LoggingHandler(loggingHandlerLevel));
                        }
                        if (compressedMessages) {
                            p.addLast(WebSocketClientCompressionHandler.INSTANCE);
                        }
                        p.addLast(new HttpObjectAggregator(8192));
                        if (DEFAULT_IDLE_TIMEOUT > 0) {
                            p.addLast(new IdleStateHandler(DEFAULT_IDLE_TIMEOUT, 0, 0));
                        }
                        WebSocketClientExtensionHandler clientExtensionHandler = 
                            getWebSocketClientExtensionHandler();
                        if (clientExtensionHandler != null) {
                            p.addLast(clientExtensionHandler);
                        }
                        p.addLast(handler);
                    }
                })
                .connect(uri.getHost(), port)
                .addListener((ChannelFuture channelFuture) -> {
                    webSocketChannel = channelFuture.channel();
                    if (!channelFuture.isSuccess()) {
                        LOG.error("Connection failed", channelFuture.cause());
                    }
                });
        } catch (Exception e) {
            LOG.error("Connection error", e);
        }
    }

    public void disconnect() {
        if (webSocketChannel != null) {
            webSocketChannel.close();
        }
        if (eventLoopGroup != null) {
            eventLoopGroup.shutdownGracefully(2, DEFAULT_IDLE_TIMEOUT, TimeUnit.SECONDS);
        }
    }

    protected WebSocketClientExtensionHandler getWebSocketClientExtensionHandler() {
        return WebSocketClientCompressionHandler.INSTANCE;
    }

    protected WebSocketClientHandler getWebSocketClientHandler(
        WebSocketClientHandshaker handshaker,
        WebSocketClientHandler.WebSocketMessageHandler handler) {
        return new WebSocketClientHandler(handshaker, handler) {
            @Override
            public void channelInactive(ChannelHandlerContext ctx) {
                LOG.info("Channel inactive");
                ctx.close();
            }

            @Override
            public void userEventTriggered(ChannelHandlerContext ctx, Object evt) {
                if (evt instanceof IdleStateEvent) {
                    IdleStateEvent e = (IdleStateEvent) evt;
                    if (e.state() == IdleState.READER_IDLE) {
                        ctx.writeAndFlush(new PingWebSocketFrame());
                    }
                }
            }
        };
    }
}

import bt.data.StorageUnit;
import bt.net.buffer.ByteBufferView;
import java.io.IOException;
import java.io.UncheckedIOException;
import java.nio.ByteBuffer;
import java.nio.channels.FileChannel;
import java.nio.file.Files;
import java.nio.file.Path;
import java.nio.file.StandardOpenOption;
import java.util.concurrent.locks.ReadWriteLock;
import java.util.concurrent.locks.ReentrantReadWriteLock;

class CachedOpenFile implements StorageUnit {
    private final FileChannel fc;
    private final ReadWriteLock lock = new ReentrantReadWriteLock();
    private final long capacity;

    CachedOpenFile(Path file, long capacity) {
        try {
            Path parent = file.getParent();
            if (parent != null) {
                Files.createDirectories(parent);
            }
            fc = FileChannel.open(file, StandardOpenOption.READ, StandardOpenOption.WRITE, StandardOpenOption.CREATE);
        } catch (IOException ex) {
            throw new UncheckedIOException("Could not open file " + file.toAbsolutePath(), ex);
        }
        this.capacity = capacity;
    }

    @Override
    public int readBlock(ByteBuffer buffer, long offset) {
        ensureOpen();

        if (offset < 0) {
            throw new IllegalArgumentException("Negative offset: " + offset);
        } else if (offset > capacity - buffer.remaining()) {
            throw new IllegalArgumentException("Received a request to read past the end of file (offset: " + offset +
                    ", requested block length: " + buffer.remaining() + ", file capacity: " + capacity);
        }

        try {
            return fc.read(buffer, offset);
        } catch (IOException e) {
            throw new UncheckedIOException("Failed to read bytes (offset: " + offset +
                    ", requested block length: " + buffer.remaining() + ", file capacity: " + capacity + ")", e);
        }
    }

    @Override
    public int writeBlock(ByteBuffer buffer, long offset) {
        ensureOpen();

        if (offset < 0) {
            throw new IllegalArgumentException("Negative offset: " + offset);
        } else if (offset > capacity - buffer.remaining()) {
            throw new IllegalArgumentException("Received a request to write past the end of file (offset: " + offset +
                    ", block length: " + buffer.remaining() + ", file capacity: " + capacity);
        }

        try {
            return fc.write(buffer, offset);
        } catch (IOException e) {
            throw new UncheckedIOException("Failed to write bytes (offset: " + offset +
                    ", block length: " + buffer.remaining() + ", file capacity: " + capacity + ")", e);
        }
    }

    @Override
    public int writeBlock(ByteBufferView buffer, long offset) {
        ensureOpen();

        if (offset < 0) {
            throw new IllegalArgumentException("Negative offset: " + offset);
        } else if (offset > capacity - buffer.remaining()) {
            throw new IllegalArgumentException("Received a request to write past the end of file (offset: " + offset +
                    ", block length: " + buffer.remaining() + ", file capacity: " + capacity);
        }

        try {
            return buffer.transferTo(fc, offset);
        } catch (IOException e) {
            throw new UncheckedIOException("Failed to write bytes (offset: " + offset +
                    ", block length: " + buffer.remaining() + ", file capacity: " + capacity + ")", e);
        }
    }

    @Override
    public long capacity() {
        return capacity;
    }

    @Override
    public long size() {
        ensureOpen();
        try {
            return fc.size();
        } catch (IOException ex) {
            throw new UncheckedIOException(ex);
        }
    }

    public void flush() throws IOException {
        if (fc.isOpen())
            this.fc.force(false);
    }

    public void lockForIoOperation() {
        this.lock.readLock().lock();
    }

    public void unlockForIoOperation() {
        this.lock.readLock().unlock();
    }

    @Override
    public void close() throws IOException {
        lock.writeLock().lock();
        try {
            this.fc.close();
        } finally {
            lock.writeLock().unlock();
        }
    }

    private void ensureOpen() {
        if (!fc.isOpen()) {
            throw new IllegalStateException("Cannot access a closed file.");
        }
    }
}

